CREATE DEFINER=`acivilatedbadmin`@`%` FUNCTION `formatCSL`(
_text TEXT
) RETURNS text CHARSET latin1
    NO SQL
BEGIN
 
IF _text IS NULL THEN
    RETURN NULL;
END IF;
 
SET _text = TRIM(_text);
 
WHILE INSTR(_text, ' ,') DO
    SET _text = REPLACE(_text, ' ,', ',');
END WHILE;
 
WHILE INSTR(_text, ', ') DO
    SET _text = REPLACE(_text, ', ', ',');
END WHILE;
 
RETURN _text;
 
END ;;
