CREATE DEFINER=`acivilatedbadmin`@`%` FUNCTION `getContractId`(
        organizationId varchar(50)) RETURNS int(11)
    READS SQL DATA
    DETERMINISTIC
BEGIN
    SELECT  parent.contract_id into @contractId
FROM organization AS node,
        organization AS parent
WHERE node.lft BETWEEN parent.lft AND parent.rgt
        AND node.organization_id = organizationId and parent.is_lead_agency=1
ORDER BY parent.lft desc limit 1;
  
  
  return @contractId;

END ;;
