CREATE DEFINER=`acivilatedbadmin`@`%` FUNCTION `getLastMessageId`(
        messageThreadId varchar(50), viewerPokketUserId varchar(500)) RETURNS text CHARSET latin1
    READS SQL DATA
    DETERMINISTIC
BEGIN

 set @messageId = null;

select pokket_user_id into @participantPokketUserId from participant where pokket_user_id in  (
select pokket_user_id from pokket_user_message_thread where message_thread_id=messageThreadId
);



select count(*) into @countOfDischargedservices from pokket_user_message_thread where message_thread_id=messageThreadId and
pokket_user_id=viewerPokketUserId and is_locked=1;


if viewerPokketUserId=@participantPokketUserId  or @countOfDischargedservices =0 then 
    
SELECT `message`.`message_id` into @messageId 
      FROM `message` inner join
	(select `pokket_user_message`.`message_id`,
	`pokket_user_message`.`pokket_user_id`,
	CONCAT(`first_name`,' ', `last_name`) as full_name
	from `pokket_user_message` inner join `pokket_user` on
	`pokket_user_message`.`pokket_user_id` =`pokket_user`.`pokket_user_id`
	where `pokket_user_message`.`message_direction`='FROM') A
    on `message`.`message_id`= A.`message_id`
    inner join
	(select `pokket_user_message`.`message_id`,
	GROUP_CONCAT(`pokket_user_message`.`pokket_user_id`) as pokket_user_ids,
	GROUP_CONCAT(`last_name`) as last_names,
    GROUP_CONCAT(`first_name`) as first_names
	from `pokket_user_message` inner join `pokket_user` on
	`pokket_user_message`.`pokket_user_id` =`pokket_user`.`pokket_user_id`
	where `pokket_user_message`.`message_direction`='TO'
    GROUP BY `pokket_user_message`.`message_id`) B
    on `message`.`message_id`= B.`message_id`
    where `message`.`message_thread_id`=messageThreadId
    
    order by `message`.`created_dtm` desc limit 1;
    

    
  else
				
                 
                 select service_id into @serviceId from pokket_user_message_thread where message_thread_id=messageThreadId and
				pokket_user_id=viewerPokketUserId and is_locked=1;
				select distinct discharged_dtm into @dischargedDatetime from participant_referral where 
				pokket_user_id=@participantPokketUserId and 
				case_manager_id =viewerPokketUserId
				and service_id=@serviceId
				and is_discharged=1;
                
                SELECT `message`.`message_id` into @messageId 
			  FROM `message` inner join
			(select `pokket_user_message`.`message_id`,
			`pokket_user_message`.`pokket_user_id`,
			CONCAT(`first_name`,' ', `last_name`) as full_name
			from `pokket_user_message` inner join `pokket_user` on
			`pokket_user_message`.`pokket_user_id` =`pokket_user`.`pokket_user_id`
			where `pokket_user_message`.`message_direction`='FROM') A
			on `message`.`message_id`= A.`message_id`
			inner join
			(select `pokket_user_message`.`message_id`,
			GROUP_CONCAT(`pokket_user_message`.`pokket_user_id`) as pokket_user_ids,
			GROUP_CONCAT(`last_name`) as last_names,
			GROUP_CONCAT(`first_name`) as first_names
			from `pokket_user_message` inner join `pokket_user` on
			`pokket_user_message`.`pokket_user_id` =`pokket_user`.`pokket_user_id`
			where `pokket_user_message`.`message_direction`='TO'
			GROUP BY `pokket_user_message`.`message_id`) B
			on `message`.`message_id`= B.`message_id`
			where `message`.`message_thread_id`=messageThreadId
            and timestampdiff(second, message.created_dtm,@dischargedDatetime) > 0
			
			order by `message`.`created_dtm` desc limit 1;
                



    end if;

    return @messageId ;

END ;;
