CREATE DEFINER=`acivilatedbadmin`@`%` FUNCTION `getSharedDocumentIds`(
        sharedBy varchar(50), sharedWith varchar(500), countOfActiveWholePlanShares tinyint(4), countOfActiveEntireFileCabinetShares tinyint(4)) RETURNS text CHARSET latin1
    READS SQL DATA
    DETERMINISTIC
BEGIN

select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=sharedWith 
		group by pokket_user_id) AS A;
        
select sharedDocuments into @sharedDocuments from (
select 1 as groupId, GROUP_CONCAT(document_id) as sharedDocuments from (
				select document_id from plan_item_document where
                participant_plan_item_id in (
                
                select plan_item_id from (
						select shared_ref_id as plan_item_id from vw_active_pokket_user_shares 
                        inner join vw_plan_items_extended on
                        vw_active_pokket_user_shares.shared_ref_id=vw_plan_items_extended.participant_plan_item_id
						where share_type_id=3 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)
                        and  vw_plan_items_extended.parent_plan_item_id is null 
                        and  vw_plan_items_extended.grandparent_plan_item_id  is null
                        union
                        select shared_ref_id as plan_item_id from vw_active_pokket_user_shares 
                        inner join vw_plan_items_extended on
                        vw_active_pokket_user_shares.shared_ref_id=vw_plan_items_extended.participant_plan_item_id
						where share_type_id=3 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)
                        and  vw_plan_items_extended.parent_plan_item_id is not null 
                        and parent_plan_item_status_id <> 4 and grandparent_plan_item_status_id <> 4
                        and  vw_plan_items_extended.grandparent_plan_item_id  is null
                        and vw_plan_items_extended.parent_plan_item_id in 
                        (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        union
						select shared_ref_id as plan_item_id from vw_active_pokket_user_shares 
                        inner join vw_plan_items_extended on
                        vw_active_pokket_user_shares.shared_ref_id=vw_plan_items_extended.participant_plan_item_id
						where share_type_id=3 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0  and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)
                        and  vw_plan_items_extended.parent_plan_item_id is not null 
                        and  vw_plan_items_extended.grandparent_plan_item_id  is not null
                        and  vw_plan_items_extended.parent_plan_item_status_id <> 4
                        and  vw_plan_items_extended.grandparent_plan_item_status_id <> 4
                         and (vw_plan_items_extended.parent_plan_item_id  in 
                         (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)) 
                        or vw_plan_items_extended.grandparent_plan_item_id  in 
                        (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)) )
						
                        union
						select participant_plan_item_id as plan_item_id from vw_plan_items_extended where pokket_user_id in (
                        select shared_ref_id from vw_active_pokket_user_shares 
						where share_type_id=1 and shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and plan_item_type_id=3 
						
                        union
						select participant_plan_item_id as plan_item_id from vw_plan_items_extended where pokket_user_id in (
                        select shared_ref_id from vw_active_pokket_user_shares 
						where share_type_id=5 and shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and plan_item_type_id=3 
                        
                        
                        union
						select participant_plan_item_id as plan_item_id from vw_plan_items_extended where need_category_id in (
                        select shared_ref_id from vw_active_pokket_user_shares 
						where share_type_id=2 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0  and shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and  vw_plan_items_extended.parent_plan_item_id is null 
                        and  vw_plan_items_extended.grandparent_plan_item_id  is null
                        union
						select participant_plan_item_id as plan_item_id from vw_plan_items_extended where need_category_id in (
                        select shared_ref_id from vw_active_pokket_user_shares 
						where share_type_id=2 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0   and shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and  vw_plan_items_extended.parent_plan_item_id is not null 
                        and parent_plan_item_status_id <> 4 and grandparent_plan_item_status_id <> 4
                        and  vw_plan_items_extended.grandparent_plan_item_id  is null
                        and vw_plan_items_extended.parent_plan_item_id not in 
                        (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
						union
						select participant_plan_item_id as plan_item_id from vw_plan_items_extended where need_category_id in (
                        select shared_ref_id from vw_active_pokket_user_shares 
						where share_type_id=2 and @countOfActiveWholePlanShares=0 and @countOfActiveEntireFileCabinetShares =0   and shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and  vw_plan_items_extended.parent_plan_item_id is not null 
                        and  vw_plan_items_extended.grandparent_plan_item_id  is not null
                        and parent_plan_item_status_id <> 4 and grandparent_plan_item_status_id <> 4
                        and vw_plan_items_extended.parent_plan_item_id not in 
                        (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds))
                        and vw_plan_items_extended.grandparent_plan_item_id not in 
                        (select shared_ref_id as plan_item_id from vw_active_pokket_user_shares  where share_type_id=3 and 
						shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)) 
                        ) A where plan_item_id not in (select shared_ref_id from pokket_user_share
					where share_type_id=3  and 
                    shared_by=sharedBy and  FIND_IN_SET(shared_with,@serviceIds)
                    and is_active=0)
                
                )
                
                and document_id not in (select shared_ref_id from pokket_user_share where shared_by=sharedBy
                and shared_with=sharedWith and share_type_id=4 and is_active=0)
                and participant_plan_item_id not in (select shared_ref_id from pokket_user_share where shared_by=sharedBy
                and shared_with=sharedWith and share_type_id=3 and is_active=0)
                UNION
                select shared_ref_id from vw_active_pokket_user_shares where shared_by=sharedBy
                and shared_with=sharedWith and share_type_id=4
                UNION
                select shared_ref_id from vw_active_pokket_user_shares where shared_by=sharedBy
                and FIND_IN_SET(shared_with,sharedWith) and share_type_id=4
) B   ) Z group by groupId;
  
  
  return @sharedDocuments;




END ;;
