				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=pokketUserId and find_in_set(shared_with,sharedWith)
				) and parent_plan_item_id is null  
				union
                select participant_plan_item_id,plan_item_type_id from vw_plan_items where  plan_item_type_id=3 and parent_plan_item_id in (
				select participant_plan_item_id from participant_plan_item where  pokket_user_id=pokketUserId and need_category_id in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=pokketUserId and find_in_set(shared_with,sharedWith)
				) ) 
				union
                select participant_plan_item_id,plan_item_type_id from vw_plan_items where  plan_item_type_id=3 and parent_plan_item_id in (
