CREATE DEFINER=`acivilatedbadmin`@`%` FUNCTION `getSharedPlanItemIdsIncludingObjectives`(
        sharedBy varchar(50), sharedWith varchar(500), countOfActiveWholePlanShares tinyint(4)) RETURNS text CHARSET latin1
    READS SQL DATA
    DETERMINISTIC
BEGIN
select sharedPlanItems into @sharedPlanItems from (
select 1 as groupId, GROUP_CONCAT(participant_plan_item_id) as sharedPlanItems from (
				select participant_plan_item_id,plan_item_type_id from vw_plan_items   where countOfActiveWholePlanShares >0
                and pokket_user_id=sharedBy
						and parent_plan_item_id is null and participant_plan_item_id not in (
						select shared_ref_id from pokket_user_share where shared_by=sharedBy
					and  find_in_set(shared_with,sharedWith) and is_active=0)
                union    
                select participant_plan_item_id,plan_item_type_id from vw_plan_items   where countOfActiveWholePlanShares >0
                and pokket_user_id=sharedBy
						and parent_plan_item_id is not null and participant_plan_item_id not in (
						select shared_ref_id from pokket_user_share where shared_by=sharedBy
					and  find_in_set(shared_with,sharedWith) and is_active=0)    
				union
				select participant_plan_item_id,plan_item_type_id  from vw_plan_items  
                inner join (select shared_ref_id from vw_active_pokket_user_shares where shared_by=sharedBy
				and find_in_set(shared_with,sharedWith)) S
                on vw_plan_items.participant_plan_item_id=S.shared_ref_id
                where countOfActiveWholePlanShares >0
                and pokket_user_id=sharedBy
				and parent_plan_item_id is not null and parent_plan_item_id not in (
				select shared_ref_id from pokket_user_share where shared_by=sharedBy
				and find_in_set(shared_with,sharedWith)  and is_active=0)
                union
				select participant_plan_item_id,plan_item_type_id  from vw_plan_items  
				where parent_plan_item_id in (select shared_ref_id from vw_active_pokket_user_shares where shared_by=sharedBy
				and find_in_set(shared_with,sharedWith))
                union
                select participant_plan_item_id,plan_item_type_id  from vw_plan_items  
                where parent_plan_item_id in (
                select participant_plan_item_id  from vw_plan_items  
				where parent_plan_item_id in (select shared_ref_id from vw_active_pokket_user_shares where shared_by=sharedBy
				and find_in_set(shared_with,sharedWith)))
				
				union
				select participant_plan_item_id,plan_item_type_id from vw_plan_items where  countOfActiveWholePlanShares =0
                and pokket_user_id=sharedBy  and need_category_id in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				) and parent_plan_item_id is null  
				union
				select participant_plan_item_id,plan_item_type_id from vw_plan_items where countOfActiveWholePlanShares =0
                and pokket_user_id=sharedBy and  
                need_category_id in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				) and
                parent_plan_item_id  not in (
				select participant_plan_item_id from participant_plan_item where  pokket_user_id=sharedBy and need_category_id in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				)  ) and
                 parent_plan_item_id  not in (
                 select participant_plan_item_id from participant_plan_item where  parent_plan_item_id in (
				select participant_plan_item_id from participant_plan_item where  pokket_user_id=sharedBy and need_category_id in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				) ))  
				union
				select participant_plan_item_id,plan_item_type_id from vw_plan_items where (need_category_id is null or need_category_id not in (
				SELECT distinct need_category_id FROM vw_active_pokket_user_shares where share_type_id=2
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				)  ) 
                and   countOfActiveWholePlanShares =0
                and participant_plan_item_id in (
				SELECT distinct shared_ref_id FROM vw_active_pokket_user_shares where share_type_id=3
				and shared_by=sharedBy and find_in_set(shared_with,sharedWith)
				 ) ) A 
  group by groupId) B;
  
  
  return @sharedPlanItems;




END ;;
