CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `CM_usageReport`(CM_ID varchar(9))
begin

SELECT 
    'Case Manager Name' AS 'Parameter',
    CONCAT(pu.first_name, ' ', pu.last_name) AS 'Value'
FROM
    pokket_user pu
WHERE
    pu.pokket_user_id = CM_ID 
UNION SELECT 
    'No Of Connected Participants' AS 'Parameter',
    COUNT(DISTINCT uc.pokket_user_id) AS 'Value'
FROM
    pokket_user_connection uc
WHERE
    uc.connection_pokket_user_id = CM_ID
        AND uc.connection_pokket_user_role_id = 4 
UNION SELECT 
    'No Of Messages Received' AS 'Parameter',
    COUNT(um.message_id) AS 'Value'
FROM
    pokket_user_message um
WHERE
    um.pokket_user_id = CM_ID
        AND um.message_direction = 'To' 
UNION SELECT 
    'No Of Messages Sent' AS 'Parameter',
    COUNT(um.message_id) AS 'Value'
FROM
    pokket_user_message um
WHERE
    um.created_by = CM_ID
        AND um.message_direction = 'To'
        AND um.created_by <> um.pokket_user_id 
UNION SELECT 
    'No Of Past Appointments' AS 'Parameter',
    COUNT(ar.appointment_request_id) AS 'Value'
FROM
    appointment_request ar
        LEFT JOIN
    appointment_request_timeslot ats ON ats.appointment_request_id = ar.appointment_request_id
WHERE
    (ar.from_pokket_user_id = CM_ID
        OR ar.to_pokket_user_id = CM_ID)
        AND ats.timeslot < DATE_FORMAT(CURRENT_TIMESTAMP,
            '%m-%d-%Y %h:%i:%s %p')
            
            union
            
            select 'No Of Future Appointments' as 'Parameter' , count(ar.appointment_request_id) as 'Value' 
from appointment_request ar 
left join appointment_request_timeslot ats on ats.appointment_request_id = ar.appointment_request_id
where (ar.from_pokket_user_id =CM_ID or ar.to_pokket_user_id =CM_ID )
and ats.timeslot >= DATE_FORMAT(CURRENT_TIMESTAMP,
                    '%m-%d-%Y %h:%i:%s %p') ;
        
end ;;
