CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `FacilityReferalStats`(org_id varchar(20), start_dt DATE ,end_dt DATE)
begin
select os.facility_name , count(fm.pokket_user_id) as 'No of case Managers' , 
case when  temp.referals is null then 0 else temp.referals end as 'No of Referrals received'
from facility_case_manager  fm 
left join organization_facility os on  fm.facility_id = os.organization_facility_id 
left join (select pr.facility_id , pr.service_id ,count(pr.pokket_user_id) 'referals' from participant_referral pr
where pr.created_dtm between start_dt and end_dt
group by facility_id , service_id ) temp on fm.facility_id = temp.facility_id
where os.organization_id = org_id
group by  os.facility_name, os.facility_name ;
end ;;
