CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `participantActivity`(P_ID varchar (9), start_dt DATE , end_dt DATE)
begin

SELECT 
    
    
    CONCAT('Received message from ',
            (SELECT 
                    CONCAT(first_name, ' ', last_name)
                FROM
                    pokket_user p
                WHERE
                    p.pokket_user_id = pm1.created_by)) AS 'Activity',
    pm1.created_dtm AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    pokket_user_message pm1 ON pu.pokket_user_id = pm1.pokket_user_id
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID)
        AND pm1.created_dtm BETWEEN start_dt AND end_dt
UNION SELECT 
   
    
    CONCAT('Sent message to ',
            (SELECT 
                    CONCAT(first_name, ' ', last_name)
                FROM
                    pokket_user p
                WHERE
                    p.pokket_user_id = pm2.pokket_user_id)) AS 'Activity',
    pm2.created_dtm AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    pokket_user_message pm2 ON pu.pokket_user_id = pm2.created_by
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID)
        AND pm2.created_dtm BETWEEN start_dt AND end_dt
UNION SELECT 
    
    
    CONCAT('Connected to ',
            (SELECT 
                    CONCAT(first_name, ' ', last_name)
                FROM
                    pokket_user p
                WHERE
                    p.pokket_user_id = uc.connection_pokket_user_id)) AS 'Activity',
    uc.connected_date AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    pokket_user_connection uc ON pu.pokket_user_id = uc.pokket_user_id
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID)
        AND uc.created_dtm BETWEEN start_dt AND end_dt
UNION SELECT 
    
    
    CONCAT('Connected to ',
            (SELECT 
                    CONCAT(first_name, ' ', last_name)
                FROM
                    pokket_user p
                WHERE
                    p.pokket_user_id = uc.pokket_user_id)) AS 'Activity',
    uc.connected_date AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    pokket_user_connection uc ON pu.pokket_user_id = uc.connection_pokket_user_id
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID)
        AND uc.created_dtm BETWEEN start_dt AND end_dt
UNION SELECT 
    

    CONCAT('Uploaded document ',
            '\'',
            a.document_name,
            '\'') AS 'Activity',
    a.created_dtm AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    (SELECT 
        pd.pokket_user_id, d.document_name, pd.created_dtm
    FROM
        pokket_user_document pd
    INNER JOIN document d ON pd.document_id = d.document_id ) AS a ON pu.pokket_user_id = a.pokket_user_id
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID) and a.created_dtm BETWEEN start_dt AND end_dt
            
	union 
    
    SELECT 
    
    
    CONCAT('Attended Event  ', pe.event_title ) as 'Activity',
    pe.starts_at_dtm AS 'Date'
FROM
    pokket_user pu
        INNER JOIN
    pokket_user_event pe ON pu.pokket_user_id = pe.pokket_user_id
WHERE
    pu.user_name = (SELECT 
            p.user_name
        FROM
            pokket_user p
        WHERE
            p.pokket_user_id = P_ID)
        AND pe.starts_at_dtm BETWEEN start_dt AND end_dt

ORDER BY 'Date';

end ;;
