CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `participantHouseHold`(IN CM_id varchar(9))
begin

SELECT 
    p1.user_name AS 'Participant UserName',
    concat(p1.first_name, ' ', p1.last_name )AS 'Participant Name',
    
    concat (
CASE
when pp.resides_with is null then 'Participant, Living alone'
else 
	CASE 
		when pp.no_of_family_children is null then 
			CASE 
				when pp.no_of_family_adults is not null then 'Participant , No Children, with Another Adult' 
                else 'Participant , Living Alone ' end
		else  CASE 
				when pp.no_of_family_adults is null  then 'Participant , With children and No Another Adult'
				else 'Participant , With children and Another Adult' end
	end 
end) as 'Household Status',
(SELECT 
            hi.household_income_description
        FROM
            ref_household_income hi
        WHERE
            hi.household_income_id = pp.household_income_id) AS 'Income Status'
    
from pokket_user p1 , pokket_user p2 , pokket_user_connection pc  , participant pp
 
 where 
 p1.pokket_user_id = pc.pokket_user_id 
 and p2.pokket_user_id = pc.connection_pokket_user_id
 and pp.pokket_user_id = p1.pokket_user_id
 and p2.pokket_user_id = CM_id;
    
end ;;
