CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `participantPerformance`(CM_ID varchar(9))
begin
SELECT 
    CONCAT(pu.first_name, ' ', pu.last_name) AS 'Particpant Name',
    COUNT(pi.parent_plan_item_id) AS 'No of delayed tasks',
    (select count(pi1.parent_plan_item_id)  from participant_plan_item pi1 where pi1.pokket_user_id = pi.pokket_user_id and pi1.completion_date is null and pi1.due_date < current_timestamp  and pi1.need_category_id = pi.need_category_id) as 'No of incomplete tasks',
    rn.need_category_description AS 'Need Category'

FROM
    participant_plan_item pi
        LEFT JOIN
    pokket_user pu ON pu.pokket_user_id = pi.pokket_user_id
        LEFT JOIN
    ref_need_category rn ON pi.need_category_id = rn.need_category_id


WHERE
    pi.pokket_user_id in (select  uc.pokket_user_id from pokket_user_connection uc where 
uc.connection_pokket_user_id = CM_ID )
        AND pi.due_date < pi.completion_date
        AND pi.need_category_id IN (SELECT DISTINCT
            need_category_id
        FROM
            pokket_user_share ps
        WHERE
            ps.shared_by = pu.pokket_user_id
                AND ps.shared_with = CM_ID
                AND CURRENT_TIMESTAMP <= CASE
                WHEN
                    ps.share_duration_id = 1
                THEN
                    DATE_ADD(ps.share_start_dtm,
                        INTERVAL share_duration_units HOUR)
                WHEN
                    ps.share_duration_id = 2
                THEN
                    DATE_ADD(ps.share_start_dtm,
                        INTERVAL share_duration_units DAY)
                WHEN
                    ps.share_duration_id = 3
                THEN
                    DATE_ADD(ps.share_start_dtm,
                        INTERVAL share_duration_units WEEK)
                WHEN
                    ps.share_duration_id = 4
                THEN
                    DATE_ADD(ps.share_start_dtm,
                        INTERVAL share_duration_units MONTH)
                ELSE DATE_ADD(ps.share_start_dtm,
                    INTERVAL share_duration_units YEAR)
            END
                AND ps.need_category_id IS NOT NULL)
GROUP BY 'Particpant Name' , pi.need_category_id , 'Need Category'
order by COUNT(pi.parent_plan_item_id) desc;

end ;;
