CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `Participant_usage`(pp_id varchar(9))
begin

SELECT 
    u.user_name AS PARTICIPANT_USENAME,
    u.first_name AS PARTICIPANT_FIRST_NAME,
    u.last_name AS PARTICIPANT_LAST_NAME,
    COUNT(CASE
        WHEN uc.connection_pokket_user_role_id = 3 THEN 1
    END) AS NO_OF_SPA_CONNECTIONS,
    COUNT(CASE
        WHEN uc.connection_pokket_user_role_id = 4 THEN 1
    END) AS NO_OF_CM_CONNECTIONS,
    COUNT(CASE
        WHEN pr1.is_accepted = 1 THEN 1
    END) AS NO_ACCEPETD_REFERALS,
    COUNT(CASE
        WHEN pr1.is_accepted = 0 THEN 1
    END) AS NO_REJECTED_REFERALS,
    (SELECT 
            COUNT(pi.participant_plan_item_id)
        FROM
            participant_plan_item pi
        WHERE
            pi.pokket_user_id = pp.pokket_user_id) AS NO_OF_TASKS,
    (SELECT 
            COUNT(pi.participant_plan_item_id)
        FROM
            participant_plan_item pi
        WHERE
            pi.pokket_user_id = pp.pokket_user_id
                AND pi.status_id = 3) AS NO_OF_COMPLETED_TASKS
    
FROM
    participant pp
        LEFT JOIN
    pokket_user u ON pp.pokket_user_id = u.pokket_user_id
        LEFT JOIN
    pokket_user_connection uc ON pp.pokket_user_id = uc.pokket_user_id
        INNER JOIN
    pokket_user_role pur ON uc.pokket_user_id = pur.pokket_user_id
        LEFT JOIN
    participant_referral pr1 ON pr1.pokket_user_id = pp.pokket_user_id
    
    where pp.pokket_user_id = pp_id
GROUP BY u.user_name;

end ;;
