CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `simpleproc`(IN CM_id varchar(9))
begin

SELECT 
    u.user_name AS CM_USERNAME,
    u.last_name AS CM_LASTNAME,
    u.first_name AS CM_FIRSTNAME,
    (SELECT 
            COUNT(DISTINCT uc.connection_pokket_user_id)
        FROM
            pokket_user_connection uc
        WHERE
            uc.pokket_user_id = u.pokket_user_id) AS NO_OF_CONNECTED_PARTICIPANTS,
    (SELECT 
            COUNT(um.message_id)
        FROM
            pokket_user_message um
        WHERE
            um.pokket_user_id = u.pokket_user_id) AS NO_OF_MESSAGES,
    (SELECT 
            COUNT(ar2.appointment_request_id)
        FROM
            appointment_request ar2,
            appointment_request_timeslot at2
        WHERE
            ar2.appointment_request_id = at2.appointment_request_id
                AND ar2.from_pokket_user_id = u.pokket_user_id
                AND at2.timeslot < DATE_FORMAT(CURRENT_TIMESTAMP,
                    '%m-%d-%Y %h:%i:%s %p')) + (SELECT 
            COUNT(ar2.appointment_request_id)
        FROM
            appointment_request ar2,
            appointment_request_timeslot at2
        WHERE
            ar2.appointment_request_id = at2.appointment_request_id
                AND ar2.to_pokket_user_id = u.pokket_user_id
                AND at2.timeslot < DATE_FORMAT(CURRENT_TIMESTAMP,
                    '%m-%d-%Y %h:%i:%s %p')) AS PAST_APPOINTMENT_COUNT,
    (SELECT 
            COUNT(ar2.appointment_request_id)
        FROM
            appointment_request ar2,
            appointment_request_timeslot at2
        WHERE
            ar2.appointment_request_id = at2.appointment_request_id
                AND ar2.from_pokket_user_id = u.pokket_user_id
                AND at2.timeslot > DATE_FORMAT(CURRENT_TIMESTAMP,
                    '%m-%d-%Y %h:%i:%s %p')) + (SELECT 
            COUNT(ar2.appointment_request_id)
        FROM
            appointment_request ar2,
            appointment_request_timeslot at2
        WHERE
            ar2.appointment_request_id = at2.appointment_request_id
                AND ar2.to_pokket_user_id = u.pokket_user_id
                AND at2.timeslot > DATE_FORMAT(CURRENT_TIMESTAMP,
                    '%m-%d-%Y %h:%i:%s %p')) AS FUTURE_APPOINTMENT_COUNT
FROM
    pokket_user u
        LEFT JOIN
    pokket_user_role r ON u.pokket_user_id = r.pokket_user_id
WHERE
    r.user_role_id = 4
        and u.pokket_user_id = CM_ID
GROUP BY u.pokket_user_id;
end ;;
