CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspAcceptRejectParticipantShare`(
		shareId varchar(50),
        isAccepted tinyint(1),
        notificationBatchId varchar(50),
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
   
   declare notificationTypeId tinyint(4); 
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
            SET displayError='';
			
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;

        if isAccepted=1 then
			set notificationTypeId=5;
		else
			set notificationTypeId=6;
        end if;    

        select shared_with, concat(first_name,' ',last_name) into @connectionPokketUserId, @connectionName
        from pokket_user inner join pokket_user_share on
        pokket_user.pokket_user_id=pokket_user_share.shared_with
        where share_id=shareId;
        
        select shared_by,email_address into @participantPokketUserId, @emailAddress
        from pokket_user inner join pokket_user_share on
        pokket_user.pokket_user_id=pokket_user_share.shared_by
        where share_id=shareId;
        
        select REPLACE(notification_text_template,'<CONNECTION_NAME>',@connectionName)
        into @notificationTextTemplate from ref_notification_type where notification_type_id=notificationTypeId;
        
        
        UPDATE `pokket_user_share`
			SET
			`is_accepted` = isAccepted,
			`modified_by` = modifiedBy,
			`modified_dtm` = CURRENT_TIMESTAMP,
            `aws_request_id`=awsRequestId
			WHERE `share_id` = shareId;
			
                
                INSERT INTO `notification`
				(`notification_type_id`,
                `notification_batch_id`,
				 `pokket_user_id`,
                 `organization_facility_id`,
                 `organization_service_id`,
                 `notification_from`,
                 `email_address`,
				`notification_text`,
				`notification_dtm`,
                `action_type_id`,
                `action_ref_id`,
                `is_notified`,
                `is_read`,
				`created_by`,
				`created_dtm`,
                `aws_request_id`)
                values(notificationTypeId,
                notificationBatchId,
                @participantPokketUserId,
                null,
                null,
                @connectionPokketUserId,
                @emailAddress,
                @notificationTextTemplate,
                CURRENT_TIMESTAMP,4,shareId,
                0,0,modifiedBy,CURRENT_TIMESTAMP,
                awsRequestId
                );
                
                
        
     COMMIT;  
     
    
     CALL uspGetItemSharedByParticipant(shareId,awsRequestId);

END ;;
