        addressTypeId tinyint(4),
        addressLine1 varchar(100),
        addressLine2 varchar(100),
        addressCity varchar(50),
        addressCounty varchar(100),
        addressState varchar(2),
        addressZip varchar(15),
        modifiedOrCreatedBy varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
            SET procError=@full_error;
            SET displayError='';
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
    SELECT count(*) INTO @addressCount FROM `pokket_user_address` 
    WHERE `pokket_user_id` = pokketUserId AND `address_type_id` = addressTypeId;
    
    
    IF @addressCount > 0 THEN
 		UPDATE `pokket_user_address`
		SET
		`address_line_1` = addressLine1,
		`address_line_2` = addressLine2,
		`address_city` = addressCity,
		`address_county` = addressCounty,
		`address_state` = addressState,
		`address_zip` = addressZip,
		`modified_by` = modifiedOrCreatedBy,
		`modified_dtm` = CURRENT_TIMESTAMP
		WHERE `pokket_user_id` = pokketUserId AND `address_type_id` = addressTypeId;
	ELSE
		INSERT INTO `pokket_user_address`
		(`pokket_user_id`,
		`address_type_id`,
		`address_line_1`,
		`address_line_2`,
		`address_city`,
		`address_county`,
		`address_state`,
		`address_zip`,
		`created_by`,
		`created_dtm`)
		VALUES
		(pokketUserId,
		addressTypeId,
		addressLine1,
		addressLine2,
		addressCity,
		addressCounty,
		addressState,
		addressZip,
		modifiedOrCreatedBy,
		CURRENT_TIMESTAMP);
    END IF;
    
   COMMIT;

END ;;
