CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspApplyTemplateToPlan`(
templateId varchar(50), serviceId varchar(50), pokketUserId varchar(50)
)
BEGIN


    DECLARE finished INTEGER DEFAULT 0;
    DECLARE participantPlanItemId varchar(50);
    DECLARE participantPlanItemDescription varchar(150);
    DECLARE parentPlanItemId varchar(50);
    DECLARE planItemTypeId tinyint(4);
    DECLARE location varchar(200);
    DECLARE dueDate varchar(30);
    DECLARE statusId tinyint(4);
    DECLARE needCategoryId tinyint(4);
    DECLARE notes varchar(10000);
	DECLARE createdBy varchar(50);
    DECLARE awsRequestId varchar(50);
    DECLARE isMandatory tinyint(1);
    DECLARE locationLat decimal(9,6);
    DECLARE locationLon decimal(9,6);
    
    
	DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
     


    DECLARE curApplyTemplate
        CURSOR FOR 
           SELECT 
    `plan_template_application`.`participant_plan_item_id`,
    `plan_template_application`.`participant_plan_item_description`,
    `plan_template_application`.`parent_plan_item_id`,
    `plan_template_application`.`plan_item_type_id`,
    `plan_template_application`.`location`,
    `plan_template_application`.`due_date`,
    `plan_template_application`.`status_id`,
    `plan_template_application`.`need_category_id`,
    `plan_template_application`.`notes`,
    `plan_template_application`.`created_by`,
    `plan_template_application`.`aws_request_id`,
    `plan_template_application`.`is_mandatory`,
    `plan_template_application`.`location_lat`,
    `plan_template_application`.`location_lon`
FROM `mypokket`.`plan_template_application` 
where template_id=templateId and service_id=serviceId and  pokket_user_id = pokketUserId
order by priority;

 
    
    
    DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET finished = 1;
        
        

        START TRANSACTION; 
        
        set @success = 1;
        SET @interval = 0;
 
    OPEN curApplyTemplate;
    
 
    getRecord: LOOP
		SET @interval = @interval + 3;
       SET @createdDateTime = date_add(current_timestamp(), INTERVAL @interval SECOND);
        FETCH curApplyTemplate INTO participantPlanItemId, participantPlanItemDescription,parentPlanItemId,
        planItemTypeId,location,dueDate,statusId,needCategoryId,notes,
        createdBy,awsRequestId,isMandatory,locationLat,locationLon;

        IF finished = 1 THEN 
            LEAVE getRecord;
        END IF;
        
        BEGIN
        
				    DECLARE EXIT HANDLER FOR SQLEXCEPTION
					BEGIN
						GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
							@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
							SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
							SET procError=@full_error;
							SET displayError='';
							ROLLBACK;
                            
						select procError,displayError;
                        set @success = @success * 0;
                        START TRANSACTION; 
						update plan_template_application   set process_result =0 where  participant_plan_item_id=participantPlanItemId and 
							template_id=templateId and service_id=serviceId and  pokket_user_id = pokketUserId;   
						COMMIT;
					END;
        

                
                

				call mypokket.uspInsertParticipantPlanItemFromTemplate(templateId, pokketUserId, uuid(), participantPlanItemId, null, null, null, null, 
												participantPlanItemDescription, parentPlanItemId, planItemTypeId, location, 
                                                dueDate, statusId, notes, needCategoryId, null, null, null, null, null, 
                                                null, serviceId, isMandatory, createdBy, awsRequestId, 0, @createdDateTime);
                                                
				update plan_template_application   set process_result =1 where  participant_plan_item_id=participantPlanItemId and 
						template_id=templateId and service_id=serviceId and  pokket_user_id = pokketUserId;                                                  
                                                
		END;											
                                    

    END LOOP getRecord;
    CLOSE curApplyTemplate;
    
		if @success = 1 then
			update plan_template set failed_participant_ids='NONE', modified_by='uspApplyTemplateToPlan', modified_dtm= current_timestamp
			where template_id=templateId and service_id=serviceId;
		
            select pokket_user.pokket_user_id, concat(pokket_user.first_name,' ',pokket_user.last_name) into @cmId, @pokketUserFullName
					from pokket_user 
                    JOIN plan_template ON plan_template.created_by = pokket_user.pokket_user_id
                    where plan_template.template_id = templateId;
			select REPLACE(notification_text_template,'<CM_NAME>',@pokketUserFullName)
						into @notificationTextTemplate from ref_notification_type where notification_type_id=32;
            INSERT INTO `notification`
				(`notification_type_id`,
				`notification_batch_id`,
				 `pokket_user_id`,
				 `notification_from`,
				 `organization_service_id`,
				`notification_text`,
				`notification_dtm`,
				`action_type_id`,
				`action_ref_id`,
				`is_notified`,
				`is_read`,
				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				values (
                32,
                UUID_SHORT(),
				pokketUserId,
				IFNULL(@cmId, 'uspApplyTemplateToPlan'),
                serviceId,
				@notificationTextTemplate,
				CURRENT_TIMESTAMP,
                null,
                templateId,
				0,
                0,
                IFNULL(@cmId, 'uspApplyTemplateToPlan'),
                CURRENT_TIMESTAMP,
                IFNULL(awsRequestId, '-NA-')
				);
		else
			update plan_template set 
			failed_participant_ids=CONCAT(IFNULL(failed_participant_ids,''),',',pokketUserId), 
			modified_by='uspApplyTemplateToPlan', modified_dtm= current_timestamp 
			where template_id=templateId and service_id=serviceId;
		end if;
    
	COMMIT;    
    

    
END ;;
