CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspApproveRejectParticipantReferral`(
		participantReferralId int(11),
        caseManagerId varchar(50),
        isAccepted tinyint(1),
        scheduleAppointmentPlanItemId varchar(50),
        notificationBatchId varchar(50),
        modifiedBy varchar(50),awsRequestId varchar(50))
BEGIN
   
	declare notificationTypeId tinyint(4);
	declare serviceProviderName varchar(100);
	declare referralStatusId tinyint(4); 
	declare notificationTypeIdForInitiator tinyint(4);
	DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;

    select pokket_user_id, created_by, service_id, facility_id
		into @participantPokketUserId, @referralInitiator, @serviceId, @facilityId 
		from participant_referral WHERE `participant_referral_id` = participantReferralId;
    
    SELECT organization_id into @organizationId 
		from organization_service where organization_service_id=@serviceId;

	if isAccepted=1 then
		set notificationTypeId=2;
		set referralStatusId = 4;
		select organization_name into serviceProviderName 
		from organization inner join pokket_user_organization
		on organization.organization_id=pokket_user_organization.organization_id
		where pokket_user_organization.pokket_user_id=caseManagerId;
	else
		set notificationTypeId=3;
		set referralStatusId = 5;
		set serviceProviderName='';
	end if;

	select concat(first_name,' ',last_name), email_address 
		into @participantFullName, @emailAddress
        from pokket_user where pokket_user_id = @participantPokketUserId;
        
	select concat(first_name,' ',last_name) into @caseManagerFullName
       from pokket_user where pokket_user_id = caseManagerId;
        
	select ref_need_category.need_category_id, need_category_description,organization_name, organization_service.service_name
        into @needCategoryId, @needCategoryDescription,@serviceProviderName, @serviceName
        from organization_service
        inner join organization on organization_service.organization_id=organization.organization_id
        inner join ref_need_category on ref_need_category.need_category_id=organization_service.need_category_id
        where organization_service.organization_service_id = @serviceId;
        
	select REPLACE(REPLACE(REPLACE(notification_text_template,'<SERVICE_PROVIDER_NAME>',@serviceProviderName),'<SERVICE_NAME>',@serviceName),'<CASE_MANAGER_NAME>',@caseManagerFullName)
        into @notificationTextTemplate from ref_notification_type where notification_type_id = notificationTypeId;
        
	select REPLACE(notification_text_template,'<PARTICIPANT_NAME>',@participantFullName)
        into @participantAssignedNotificationTextTemplate from ref_notification_type where notification_type_id = 13;

	UPDATE `participant_referral`
		SET
		`case_manager_id` = caseManagerId,
		`is_service_assigned_to_participant`=isAccepted,
		`is_accepted` = isAccepted,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
		`aws_request_id`= awsRequestId,
		`responded_dtm` = CURRENT_TIMESTAMP,
		`referral_status_id` = referralStatusId
		WHERE `participant_referral_id` = participantReferralId;
                
	UPDATE `participant_need`
		SET
		`is_referral_accepted` = isAccepted,
		`modified_by` = modifiedBy,
		`modified_dtm` = CURRENT_TIMESTAMP,
		`aws_request_id`=awsRequestId
		WHERE `pokket_user_id` = @participantPokketUserId
		and `need_category_id`=@needCategoryId
		and `is_referral_accepted`=0;              
              
	if isAccepted=1 then
		select count(*) into @participantCaseManagerConnections 
			from `pokket_user_connection` where
			`pokket_user_id`=@participantPokketUserId
			and `connection_pokket_user_id`=caseManagerId
			and `connection_pokket_user_role_id`=4;
			
		select count(*) into @cmParticipantConnections 
			from `pokket_user_connection` where
			`pokket_user_id`=caseManagerId
			and `connection_pokket_user_id`=@participantPokketUserId
			and `connection_pokket_user_role_id`=5;
		
		if  @participantCaseManagerConnections =0 then
			INSERT INTO `pokket_user_connection`
				(`pokket_user_id`,
				`connection_pokket_user_id`,
				`connection_pokket_user_role_id`,
				`connected_date`,
				`is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(@participantPokketUserId,
				caseManagerId,
				4,
				CURRENT_TIMESTAMP,
				1,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
				
		else
			UPDATE `pokket_user_connection`
				SET
				`connected_date` = CURRENT_TIMESTAMP,
				`is_active` = 1,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId
				WHERE `pokket_user_id` = @participantPokketUserId
				and `connection_pokket_user_id`=caseManagerId
				and `connection_pokket_user_role_id`=4;
		end if;
			
		if  @cmParticipantConnections =0 then
			INSERT INTO `pokket_user_connection`
				(`pokket_user_id`,
				`connection_pokket_user_id`,
				`connection_pokket_user_role_id`,
				`connected_date`,
				`is_active`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(caseManagerId,
				@participantPokketUserId,
				5,
				CURRENT_TIMESTAMP,
				1,
				modifiedBy,
				CURRENT_TIMESTAMP,awsRequestId);
				
		else
			UPDATE `pokket_user_connection`
				SET
				`connected_date` = CURRENT_TIMESTAMP,
				`is_active` = 1,
				`modified_by` = modifiedBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId
				WHERE `pokket_user_id` = caseManagerId 
				and `connection_pokket_user_id`=@participantPokketUserId
				and `connection_pokket_user_role_id`=5;
		end if;
			
	end if;
            
	Update notification
		set is_read = 1,
		modified_by = modifiedBy, 
		modified_dtm = CURRENT_TIMESTAMP
		where action_ref_id = participantReferralId;

	
	INSERT INTO `notification`
		(`notification_type_id`,
		`notification_batch_id`,
		 `pokket_user_id`,
		 `organization_facility_id`,
		 `organization_service_id`,
		 `notification_from`,
		 `email_address`,
		`notification_text`,
		`notification_dtm`,
		`is_notified`,
		`is_read`,
		`action_ref_id`,
		`created_by`,
		`created_dtm`,
		`aws_request_id`)
		values(
		notificationTypeId,
		notificationBatchId,
		@participantPokketUserId,
		@facilityId,
		@serviceId,
		modifiedBy,
		@emailAddress,
		@notificationTextTemplate,
		CURRENT_TIMESTAMP,
		0,0,
		participantReferralId,
		modifiedBy,
        CURRENT_TIMESTAMP,
        awsRequestId
		);
                
	if isAccepted=1 then
		
		INSERT INTO `notification`
			(`notification_type_id`,
			`notification_batch_id`,
			 `pokket_user_id`,
			 `organization_facility_id`,
			 `organization_service_id`,
			 `notification_from`,
			 `email_address`,
			`notification_text`,
			`notification_dtm`,
			`is_notified`,
			`is_read`,
			`action_ref_id`,
			`created_by`,
			`created_dtm`,`aws_request_id`)
			values(
			13,
			notificationBatchId,
			caseManagerId,
			@facilityId,
			@serviceId,
			modifiedBy,
			NULL,
			@participantAssignedNotificationTextTemplate,
			CURRENT_TIMESTAMP,
			0,0,
            participantReferralId,modifiedBy,
            CURRENT_TIMESTAMP,
            awsRequestId
			);
	end if;
            
	
	if @participantPokketUserId != @referralInitiator then
		if isAccepted=1 then
			set notificationTypeIdForInitiator = 37;
		else
			set notificationTypeIdForInitiator = 38;
		end if;
		select REPLACE(REPLACE(REPLACE(notification_text_template,'<SERVICE_PROVIDER_NAME>',@serviceProviderName),'<SERVICE_NAME>',@serviceName),'<PARTICIPANT_NAME>',@participantFullName)
				into @notificationTemplateForInitiator from ref_notification_type where notification_type_id=notificationTypeIdForInitiator;

		INSERT INTO `notification`
			(`notification_type_id`,
			`notification_batch_id`,
			 `pokket_user_id`,
			 `organization_facility_id`,
			 `organization_service_id`,
			 `notification_from`,
			 `email_address`,
			`notification_text`,
			`notification_dtm`,
			`is_notified`,
			`is_read`,
			`action_ref_id`,
			`created_by`,
			`created_dtm`,
			`aws_request_id`)
			values(
			notificationTypeIdForInitiator,
			notificationBatchId,
			@referralInitiator,
			@facilityId,
			@serviceId,
			modifiedBy,
			NULL,
			@notificationTemplateForInitiator,
			CURRENT_TIMESTAMP,
			0,
			0,
			participantReferralId,
			modifiedBy,
			CURRENT_TIMESTAMP,
			awsRequestId
			);
	end if;
                
	if isAccepted=1 then
		  INSERT INTO `participant_plan_item`
			(`participant_plan_item_id`,
			`participant_plan_item_description`,
			`pokket_user_id`,
			`parent_plan_item_id`,
			`plan_item_type_id`,
			`location`,
			`due_date`,
			`status_id`,
			`need_category_id`,
			`repeat_interval`,
			`repeat_type_id`,
			`service_plan_item_id`,
			`severity_id`,
			`priority`,
			`action_type_id`,
			`connection_pokket_user_id`,
			`created_by`,
			`created_dtm`,`aws_request_id`)
			VALUES
			(scheduleAppointmentPlanItemId,
			concat('Schedule Appointment with ',@caseManagerFullName, ' at ', serviceProviderName),
			@participantPokketUserId,
			null,
			3,
			null,
			date_format(DATE_ADD(CURRENT_TIMESTAMP,INTERVAL 2 DAY), '%m/%d/%Y'),
			1,
			@needCategoryId,
			0,
			'NONE',
			null,
			null,
			1,
			5,
			caseManagerId,
			modifiedBy,
			CURRENT_TIMESTAMP,awsRequestId);
		
		call mypokket.uspInsertPokketUserShare(UUID(), @participantPokketUserId, @serviceId, scheduleAppointmentPlanItemId, 3, 5, 1, @needCategoryId, UUID(), modifiedBy, awsRequestId);

   end if;                
                
	if isAccepted=1 then
		update pokket_user_share set is_active=1,modified_by=modifiedBy,modified_dtm=CURRENT_TIMESTAMP
		where share_type_id=4 and shared_ref_id in (select document_id from pokket_user_document
		where doc_category_id=2 and doc_category_ref_id=participantReferralId);
	end if;
        
COMMIT;
    
	 CALL uspGetParticipantReferral(@participantPokketUserId, @facilityId, @serviceId, awsRequestId);
     CALL uspGetPokketUser(modifiedBy,modifiedBy,awsRequestId);
	 CALL uspGetPokketUser(@participantPokketUserId,@participantPokketUserId,awsRequestId);
     CALL uspGetOrganizationById(@organizationId, modifiedBy, awsRequestId);
     CALL uspGetPokketUser(caseManagerId,caseManagerId,awsRequestId);

END ;;
