CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspAssignOrgForPokketUser`(
		pokketUserId varchar(50),
        organizationId varchar(50),
        organizationName varchar(100),
        organizationTypeId tinyint(4),
        organizationStatusId tinyint(4),
        jurisdictionCode varchar(50),
        contractId varchar(50),
        physicalAddressLine1 varchar(100),
        physicalAddressLine2 varchar(100),
        physicalAddressCity varchar(50),
        physicalAddressCounty varchar(100),
        physicalAddressState varchar(2),
        physicalAddressZip varchar(15),
        mailingAddressLine1 varchar(100),
        mailingAddressLine2 varchar(100),
        mailingAddressCity varchar(50),
        mailingAddressCounty varchar(100),
        mailingAddressState varchar(2),
        mailingAddressZip varchar(15),
        createdBy varchar(50))
BEGIN
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
    
	
        
     INSERT INTO `organization`
		(`organization_id`,
		`organization_name`,
		`organization_type_id`,
		`organization_status_id`,
		`jurisdiction_code`,
		`contract_id`,
		`created_by`)
		VALUES
		(organizationId,
		 organizationName,
		 organizationTypeId,
		 organizationStatusId,
		 jurisdictionCode,
		 contractId,
		 createdBy);
         
   IF(physicalAddressLine1 IS NOT NULL) THEN
		
			INSERT INTO `organization_address`
		(`organization_id`,
		`address_type_id`,
		`address_line_1`,
		`address_line_2`,
		`address_city`,
		`address_county`,
		`address_state`,
		`address_zip`,
		`created_by`)
		VALUES
		(organizationId,
		1,
		physicalAddressLine1,
		physicalAddressLine2,
		physicalAddressCity,
		physicalAddressCounty,
		physicalAddressState,
		physicalAddressZip,
		createdBy);
		
    END IF;
    IF(mailingAddressLine1 IS NOT NULL) THEN
		
				INSERT INTO `organization_address`
		(`organization_id`,
		`address_type_id`,
		`address_line_1`,
		`address_line_2`,
		`address_city`,
		`address_county`,
		`address_state`,
		`address_zip`,
		`created_by`)
		VALUES
		(organizationId,
		2,
		mailingAddressLine1,
		mailingAddressLine2,
		mailingAddressCity,
		mailingAddressCounty,
		mailingAddressState,
		mailingAddressZip,
