CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspCompleteNeedsAssessment`(
		participantPokketUserId varchar(50),
        needCategoryIds varchar(500),
       
       
        createdBy varchar(50),awsRequestId varchar(50))
BEGIN
    DECLARE formattedNeedCategoryIds varchar(500);
  
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = 'UserId already exists.';
				ELSE
					set displayError = 'An account with the email address already exists.';
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;
       
	select `participant_plan_item_id` into @completeNeedsAssessmentPlanItemId
       from `participant_plan_item` where `pokket_user_id`=participantPokketUserId
       and action_type_id=1;

        SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        
        
        select formatCSL(needCategoryIds) into formattedNeedCategoryIds; 
       
 		WHILE formattedNeedCategoryIds != '' > 0 DO
			SET @needCategoryId = SUBSTRING_INDEX(formattedNeedCategoryIds, @separator, 1);
           
 			
            select count(*) into @existingCount from `participant_need`
            where `pokket_user_id`= participantPokketUserId and `need_category_id`=@needCategoryId;
            if @existingCount = 0 then
				select need_category_description into @needCategoryDescription  from `ref_need_category`
                where `need_category_id`=@needCategoryId;
                
				INSERT INTO `participant_need`
				(`pokket_user_id`,
				`need_category_id`,
				`is_mandated`,
                `is_referral_sent`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(participantPokketUserId,
				@needCategoryId,
				0,
				0,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);
                
                 
                 
          
		  
          
			
            end if;
            
            
			SET formattedNeedCategoryIds = SUBSTRING(formattedNeedCategoryIds, CHAR_LENGTH(@needCategoryId) + @separatorLength + 1);
           
		END WHILE;   
        
          
        
		
		
		
		
        
		
        
       
     
        
     COMMIT;  
     
    

END ;;
