CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspCompleteSearchServiceProvider`(
		participantPokketUserId varchar(50),
        facilityIds varchar(1000),
        serviceId varchar(50),
        serviceIntakeResponse mediumtext,
        notificationBatchId varchar(50),
        createdBy varchar(50),
        awsRequestId varchar(50), 
        attachmentsNames varchar(1000),
        attachmentUrls varchar(1000),
        existingReferralId int(11))
BEGIN
    DECLARE formattedFacilityIds varchar(1000);
    DECLARE formattedAttachmentsNames varchar(1000);
    DECLARE formattedAttachmentUrls varchar(1000);
    
    DECLARE procError varchar(1000);
    DECLARE displayError varchar(1000);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, 
			@errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
			SET @full_error = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
			SET procError = @full_error;
			IF @errno = 1062 then 
				if  INSTR(@text, 'PRIMARY') > 0 THEN
					set displayError = procError;
				ELSE
					set displayError = procError;
				END IF;
			END IF;
        ROLLBACK;
        select procError,displayError;
 	END;
    
    START TRANSACTION;

		select need_category_id into @needCategoryId from organization_service
			where organization_service_id=serviceId;

        select concat(first_name,' ',last_name) into @participantFullName
			from pokket_user where pokket_user_id=participantPokketUserId;

		IF participantPokketUserId != createdBy THEN
			select concat(first_name,' ',last_name, ' (on behalf of ', @participantFullName, ')') into @participantFullName
				from pokket_user where pokket_user_id = createdBy;
		END IF;
        
        select REPLACE(notification_text_template,'<PARTICIPANT_NAME>',@participantFullName)
			into @notificationTextTemplate from ref_notification_type where notification_type_id=1;
        
        SET @separator = ',';
		SET @separatorLength = CHAR_LENGTH(@separator);
        
        select count(*) into @countOfIntakeResponse from `service_intake_response`
			where service_id = serviceId and pokket_user_id = participantPokketUserId;
        
        if @countOfIntakeResponse = 0 then
			INSERT INTO `service_intake_response`
				(`service_id`,
				`pokket_user_id`,
				`service_intake_response`,
				`created_by`,
				`created_dtm`,`aws_request_id`)
				VALUES
				(serviceId,
				participantPokketUserId,
				serviceIntakeResponse,
				createdBy,
				CURRENT_TIMESTAMP,awsRequestId);
        ELSE
        
			UPDATE `service_intake_response`
            SET 
				service_intake_response = serviceIntakeResponse,
                created_by = createdBy,
                created_dtm = CURRENT_TIMESTAMP,
                aws_request_id = awsRequestId
			WHERE
				service_id = serviceId 
                and pokket_user_id = participantPokketUserId;
        end if;
        
        if existingReferralId is null then
        
			select formatCSL(facilityIds) into formattedFacilityIds; 
			WHILE formattedFacilityIds != '' > 0 DO
				SET @facilityId = SUBSTRING_INDEX(formattedFacilityIds, @separator, 1);
				
				select count(*) into @existingCount from `participant_referral`
					where `pokket_user_id`= participantPokketUserId 
						and `facility_id`=@facilityId 
						and service_id= serviceId 
                        and is_discharged = 0;
				if @existingCount = 0 then
					INSERT INTO `participant_referral`
						(`pokket_user_id`,
						`facility_id`,
						`service_id`,
						 `created_by`,
						`created_dtm`,
						`aws_request_id`,
						`referral_status_id`)
						VALUES
						(participantPokketUserId,
						@facilityId,
						serviceId,
						createdBy,
						CURRENT_TIMESTAMP,
						awsRequestId,
						3);
					
					SELECT LAST_INSERT_ID() into @participantReferralId;
					
					select formatCSL(attachmentsNames) into formattedAttachmentsNames; 
					select formatCSL(attachmentUrls) into formattedAttachmentUrls; 
					WHILE formattedAttachmentUrls != '' > 0 DO
						SET @attachmentUrl = SUBSTRING_INDEX(formattedAttachmentUrls, @separator, 1);
						SET @attachmentName = SUBSTRING_INDEX(formattedAttachmentsNames, @separator, 1);
						
						
						
						
						
						SET @existingDocumentId = null;
						select document_id into @existingDocumentId from `document` where `document_url`= @attachmentUrl and `is_active`= 1 and is_quarantined= 0 order by created_dtm desc limit 1;
						if @existingDocumentId is null then
							set @docId = UUID();
							call mypokket.uspInsertPokketUserDocument(@docId, @attachmentName, @attachmentUrl, participantPokketUserId, NULL, serviceId, createdBy, awsRequestId, 2, @participantReferralId);
							INSERT INTO `participant_referral_document`
								(`referral_id`,
								`document_id`,
								`created_by`,
								`created_dtm`,
								`aws_request_id`)
								VALUES
								(@participantReferralId,
								@docId,
								createdBy,
								CURRENT_TIMESTAMP,
								awsRequestId);     
							set @shareId = UUID();
							call mypokket.uspInsertPokketUserShare(@shareId, participantPokketUserId, serviceId, @docId, 4, 5, 1, null,notificationBatchId, createdBy,awsRequestId);
						ELSE
							INSERT INTO `participant_referral_document`
								(`referral_id`,
								`document_id`,
								`created_by`,
								`created_dtm`,
								`aws_request_id`)
								VALUES
								(@participantReferralId,
								@existingDocumentId,
								createdBy,
								CURRENT_TIMESTAMP,
								awsRequestId);     
							set @shareId = UUID();
							call mypokket.uspInsertPokketUserShare(@shareId, participantPokketUserId, serviceId, @existingDocumentId, 4, 5, 1, null,notificationBatchId, createdBy,awsRequestId);
						end if;                    						
						
						SET formattedAttachmentUrls = SUBSTRING(formattedAttachmentUrls, CHAR_LENGTH(@attachmentUrl) + @separatorLength + 1);
						SET formattedAttachmentsNames = SUBSTRING(formattedAttachmentsNames, CHAR_LENGTH(@attachmentName) + @separatorLength + 1);

					END WHILE;    
					
					INSERT INTO `notification`
						(
						`notification_type_id`,
						`notification_batch_id`,
						 `pokket_user_id`,
						 `organization_facility_id`,
						 `organization_service_id`,
						 `notification_from`,
						 `email_address`,
						`notification_text`,
						`notification_dtm`,
						`is_notified`,
						`is_read`,
						`action_ref_id`,
						`created_by`,
						`created_dtm`,
						`aws_request_id`)
						select 
                        1,
                        notificationBatchId,
                        pokket_user.pokket_user_id,
                        @facilityId,
                        serviceId,
						participantPokketUserId,
                        pokket_user.email_address,
                        REPLACE(@notificationTextTemplate,'<SERVICE_NAME>',organization_service.service_name),
						CURRENT_TIMESTAMP,
                        0,0,
                        @participantReferralId,
                        createdBy,
                        CURRENT_TIMESTAMP,
                        awsRequestId 
						from pokket_user inner join pokket_user_role
						on pokket_user.pokket_user_id=pokket_user_role.pokket_user_id
						inner join 
						pokket_user_organization
						on pokket_user.pokket_user_id=pokket_user_organization.pokket_user_id
						inner join organization_facility 
						on organization_facility.organization_id=pokket_user_organization.organization_id
						inner join facility_service
						on facility_service.facility_id=organization_facility.organization_facility_id 
						inner join organization_service
						on organization_service.organization_service_id=facility_service.service_id 
						inner join ref_need_category on 
						organization_service.need_category_id=ref_need_category.need_category_id 
						where organization_facility.organization_facility_id=@facilityId
						and organization_service.organization_service_id= serviceId
						and pokket_user_role.user_role_id=3;
					
					UPDATE `participant_need`
						SET
						`is_referral_sent` = 1,
						`modified_by` = createdBy,
						`modified_dtm` = CURRENT_TIMESTAMP,
						`aws_request_id`=awsRequestId 
						WHERE `pokket_user_id` = participantPokketUserId
						and `need_category_id`=@needCategoryId
						and `is_referral_sent`=0;
				end if;
				SET formattedFacilityIds = SUBSTRING(formattedFacilityIds, CHAR_LENGTH(@facilityId) + @separatorLength + 1);
				
			END WHILE;
        else
        
			select formatCSL(attachmentsNames) into formattedAttachmentsNames; 
			select formatCSL(attachmentUrls) into formattedAttachmentUrls; 
			WHILE formattedAttachmentUrls != '' > 0 DO
				SET @attachmentUrl = SUBSTRING_INDEX(formattedAttachmentUrls, @separator, 1);
				SET @attachmentName = SUBSTRING_INDEX(formattedAttachmentsNames, @separator, 1);
				
				
				
				
				
				SET @existingDocumentId = null;
				select document_id into @existingDocumentId from `document` where `document_url`= @attachmentUrl and `is_active`= 1 and is_quarantined= 0 order by created_dtm desc limit 1;
				if @existingDocumentId is null then
					set @docId = UUID();
					call mypokket.uspInsertPokketUserDocument(@docId, @attachmentName, @attachmentUrl, participantPokketUserId, NULL, serviceId, createdBy, awsRequestId, 2, existingReferralId);
					INSERT INTO `participant_referral_document`
						(`referral_id`,
						`document_id`,
						`created_by`,
						`created_dtm`,
						`aws_request_id`)
						VALUES
						(existingReferralId,
						@docId,
						createdBy,
						CURRENT_TIMESTAMP,
						awsRequestId);     
					set @shareId = UUID();
					call mypokket.uspInsertPokketUserShare(@shareId, participantPokketUserId, serviceId, @docId, 4, 5, 1, null,notificationBatchId, createdBy,awsRequestId);
				ELSE
					INSERT INTO `participant_referral_document`
						(`referral_id`,
						`document_id`,
						`created_by`,
						`created_dtm`,
						`aws_request_id`)
						VALUES
						(existingReferralId,
						@existingDocumentId,
						createdBy,
						CURRENT_TIMESTAMP,
						awsRequestId);     
					set @shareId = UUID();
					call mypokket.uspInsertPokketUserShare(@shareId, participantPokketUserId, serviceId, @existingDocumentId, 4, 5, 1, null,notificationBatchId, createdBy,awsRequestId);
				end if;                    						
				
				SET formattedAttachmentUrls = SUBSTRING(formattedAttachmentUrls, CHAR_LENGTH(@attachmentUrl) + @separatorLength + 1);
				SET formattedAttachmentsNames = SUBSTRING(formattedAttachmentsNames, CHAR_LENGTH(@attachmentName) + @separatorLength + 1);
			END WHILE;
            
			INSERT INTO `notification`
				(
				`notification_type_id`,
				`notification_batch_id`,
				 `pokket_user_id`,
				 `organization_facility_id`,
				 `organization_service_id`,
				 `notification_from`,
				 `email_address`,
				`notification_text`,
				`notification_dtm`,
				`is_notified`,
				`is_read`,
				`action_ref_id`,
				`created_by`,
				`created_dtm`,
				`aws_request_id`)
				select 
                1,
                notificationBatchId,
                pokket_user.pokket_user_id,
				participant_referral.facility_id,
				participant_referral.service_id,
				participant_referral.pokket_user_id,
				pokket_user.email_address,
                REPLACE(@notificationTextTemplate,'<SERVICE_NAME>',organization_service.service_name),
				CURRENT_TIMESTAMP,
                0,0,
                existingReferralId,
                createdBy,
                CURRENT_TIMESTAMP,
                awsRequestId 
				from pokket_user 
                inner join pokket_user_role on pokket_user.pokket_user_id=pokket_user_role.pokket_user_id
				inner join pokket_user_organization on pokket_user.pokket_user_id=pokket_user_organization.pokket_user_id
				inner join organization_facility on organization_facility.organization_id=pokket_user_organization.organization_id
				inner join facility_service on facility_service.facility_id=organization_facility.organization_facility_id 
				inner join organization_service on organization_service.organization_service_id=facility_service.service_id 
				inner join ref_need_category on organization_service.need_category_id=ref_need_category.need_category_id 
                inner join participant_referral on (organization_facility.organization_facility_id = participant_referral.facility_id
					AND organization_service.organization_service_id = participant_referral.service_id)
				where participant_referral.participant_referral_id = existingReferralId
					and pokket_user_role.user_role_id=3;
			
			UPDATE `participant_need`
				SET
				`is_referral_sent` = 1,
				`modified_by` = createdBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
				`aws_request_id`=awsRequestId 
				WHERE `pokket_user_id` = participantPokketUserId
				and `need_category_id`=@needCategoryId
				and `is_referral_sent`=0;
			
        end if;

        select count(*) into @countOfUnsentReferrals from `participant_need`
			where `pokket_user_id` = participantPokketUserId and `is_referral_sent`=0;

		select `participant_plan_item_id` into @completeNeedsAssessmentPlanItemId
		   from `participant_plan_item` where `pokket_user_id`=participantPokketUserId
		   and action_type_id=1;
       
		   if @countOfUnsentReferrals =0 then
			   UPDATE `participant_plan_item`
				SET
				`completion_date` = CURRENT_TIMESTAMP,
				`status_id` = 3,
				`modified_by` = createdBy,
				`modified_dtm` = CURRENT_TIMESTAMP,
                `aws_request_id`=awsRequestId 
				WHERE `participant_plan_item_id` = @completeNeedsAssessmentPlanItemId;
			end if;
	
     COMMIT;
     
     call uspGetParticipantReferral(participantPokketUserId, @facilityId, serviceId, awsRequestId);

END ;;
