CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspExecuteSearch`(
		searchString varchar(100),searchType tinyint(1), skip tinyint(4), pageSize INT,
         awsRequestId varchar(50), viewerPokketUserId varchar(50))
BEGIN

    select organization_id into @viewerOrgId from pokket_user_organization
		where pokket_user_id=viewerPokketUserId;

    select contract_id, lft,rgt into @contractId, @lft, @rgt from organization where organization_id=@viewerOrgId;


if searchType = 1 then  
	select * from (
		select 
			pokket_user.pokket_user_id as pokketUserId,
			first_name as firstName,
            last_name as lastName,
            pokket_user_status_id as statusId,
			user_name as userName,
			pokket_user.ssn as agencyId,
			is_temp_password_reset as isTempPasswordReset,
			user_roles AS roles,
			organization_id as organizationId,
            participant.is_guardian as isGuardian
		FROM pokket_user 
        inner join mypokket.vw_pokket_user_role on pokket_user.pokket_user_id = vw_pokket_user_role.pokket_user_id
        left join participant on pokket_user.pokket_user_id = participant.pokket_user_id
		where organization_id in 
			(select organization_id from organization 
				where organization_status_id !=6 
                 and organization_type_id=2 
                 and lft >= @lft and rgt <= @rgt)
		 and (first_name like concat('%',searchString, '%') 
				or last_name like concat('%',searchString, '%') 
				or pokket_user.ssn like concat('%',searchString, '%') 
				or user_name like concat('%',searchString, '%'))
		union
		select
			pokket_user.pokket_user_id as pokketUserId,
            first_name as firstName,
            last_name as lastName,
            pokket_user_status_id as statusId,
			user_name as userName,
			pokket_user.ssn as agencyId,
            is_temp_password_reset as isTempPasswordReset,
            'PARTICIPANT' as roles,
			vw_pokket_user_organization.organization_id as organizationId,
            participant.is_guardian as isGuardian
		from pokket_user 
		inner join vw_pokket_user_organization on pokket_user.pokket_user_id = vw_pokket_user_organization.pokket_user_id
		left join participant on pokket_user.pokket_user_id = participant.pokket_user_id
        where `pokket_user`.`pokket_user_status_id` !=6
        and (first_name like concat('%',searchString, '%')
				or last_name like concat('%',searchString, '%') 
				or pokket_user.ssn like concat('%',searchString, '%') 
				or user_name like concat('%',searchString, '%'))
		and pokket_user.pokket_user_id in ( 
			SELECT pokket_user_id from (
				select participant_referral.pokket_user_id
					from participant_referral
					inner join pokket_user_organization on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
					where (pokket_user_organization.organization_id = @viewerOrgId or @viewerOrgId = 'acivilate')
					and participant_referral.is_service_assigned_to_participant = 1 
					and participant_referral.is_accepted = 1
					and participant_referral.is_onboarded is null
				union
				select participant_referral.pokket_user_id 
					from participant_referral
                    inner join pokket_user_organization on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
					where pokket_user_organization.organization_id = @viewerOrgId
                    and participant_referral.is_service_assigned_to_participant = 1 
					and participant_referral.is_accepted = 1
                    and participant_referral.is_onboarded is null
                    and participant_referral.pokket_user_id in (
						select participant_referral.pokket_user_id
                        from participant_referral
                        inner join participant on participant_referral.pokket_user_id=participant.pokket_user_id
						inner join pokket_user_organization on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
						where pokket_user_organization.organization_id = @viewerOrgId
                        and participant_referral.is_service_assigned_to_participant = 1 
						and participant_referral.is_accepted is null
                        and participant_referral.is_onboarded = 1
                        and (participant.contract_id = @contractId or @viewerOrgId = 'acivilate'))
				union
				select participant_referral.pokket_user_id
					from participant_referral
					inner join participant on participant_referral.pokket_user_id=participant.pokket_user_id
					inner join pokket_user_organization on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
					where pokket_user_organization.organization_id in (
						select organization_id from organization 
						where organization_status_id !=6
                        and organization_type_id=2
                        and lft >= @lft and rgt <= @rgt)
					and participant_referral.is_service_assigned_to_participant = 1 
					and participant_referral.is_accepted is null
                    and	participant_referral.is_onboarded = 1
                    and (participant.contract_id = @contractId or @viewerOrgId = 'acivilate')
				union
				select participant_referral.pokket_user_id
					from participant_referral
					inner join participant on participant_referral.pokket_user_id=participant.pokket_user_id
					inner join pokket_user_organization on participant_referral.case_manager_id=pokket_user_organization.pokket_user_id
					where participant_referral.is_accepted is null
					and participant_referral.is_discharged=1
					and participant_referral.is_onboarded =1
                    and ( organization_id = @viewerOrgId or @viewerOrgId = 'acivilate')        
				union
				select distinct pokket_user_id from pokket_user 
					where pokket_user_id in (
						select pokket_user_id from participant_referral
							where case_manager_id in 
								(select pokket_user_id from pokket_user_organization 
									where (organization_id = @viewerOrgId or @viewerOrgId = 'acivilate'))
							and is_service_assigned_to_participant=1)
		   ) A ) ) B
	 order by lastName
	 LIMIT pageSize OFFSET skip;
end if; 


if searchType = 2 then
	select organization_id as organizationId, organization_name as name, organization_status_id as statusId
	from 
		(select * from organization 
		where organization_status_id != 6 
         and organization_type_id = 2 
         and lft >= @lft and rgt <= @rgt) A
	where organization_name like concat('%',searchString, '%') 
	order by organization_name
	LIMIT pageSize OFFSET skip;
end if; 

END ;;
