CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGenerateChangePayloadForLambda`(
tableName varchar(50), changeType varchar(50)
)
BEGIN
DECLARE num_rows INT DEFAULT 0;
DECLARE col_name VARCHAR(150);
DECLARE col_names CURSOR FOR
  SELECT column_name
  FROM INFORMATION_SCHEMA.COLUMNS
  WHERE table_name = tableName
  ORDER BY ordinal_position;

OPEN col_names;
select FOUND_ROWS() into num_rows;

SET @i = 1;
SET @payload = CONCAT('CONCAT(\'{"table_name":"',tableName, '","change_type":"', changeType, '",');

the_loop: LOOP
   IF @i > num_rows THEN
        CLOSE col_names;
        LEAVE the_loop;
    END IF;


    FETCH col_names 
    INTO col_name;     
    
    
    SET @payload = CONCAT(@payload, '"new_', col_name, '" : "\'', ',if(isnull(', 'NEW.', col_name,') OR ', 'NEW.', col_name, '= \'\',\'\', ', 'REPLACE(NEW.', col_name, ',\'\"\',\'\\\\"\')), \'",');
    if (changeType = 'UPDATE') THEN
		SET @payload = CONCAT(@payload, '"old_', col_name, '" : "\'', ',if(isnull(', 'OLD.', col_name,') OR ', 'OLD.', col_name, '= \'\',\'\', ', 'REPLACE(OLD.', col_name, ',\'\"\',\'\\\\"\')), \'",');
    end if;



    SET @i = @i + 1;  
END LOOP the_loop;

set @payload = SUBSTRING(@payload, 1, CHAR_LENGTH(@payload)-1);

set @payload = CONCAT(@payload, '\}\')');

select @payload; 

END ;;
