CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetAppointmentRequest`(
		appointmentRequestId varchar(50), awsRequestId varchar(50))
BEGIN
	SELECT `appointment_request`.`appointment_request_id` as appointmentRequestId,
			`appointment_request`.`from_pokket_user_id` as fromPokketUserId,
            A.first_name as  fromPokketUserFirstName,
            A.last_name as fromPokketUserLastName,
            (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=A.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS fromPokketUserRole,
			B.first_name as  toPokketUserFirstName,
            B.last_name as toPokketUserLastName,
            (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=B.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS toPokketUserRole,
			A.profile_image_url as fromProfileImageUrl,
            B.profile_image_url as toProfileImageUrl,
            CONCAT(F.first_name,' ',F.last_name) as createdBy,
            F.pokket_user_id as createdByPokketUserId,
			`appointment_request`.`created_dtm` as requestedDateTime,
			`appointment_request`.`accept_dtm` as acceptedDateTime,
			`appointment_request`.`to_pokket_user_id` as toPokketUserId,
			`appointment_request`.`meeting_purpose` as meetingPurpose,
            `appointment_request`.`request_note` as requestNote,
            `appointment_request`.`accept_note` as acceptNote,
            `appointment_request`.`reject_note` as rejectNote,
			`appointment_request`.`appointment_request_status_id` as appointmentRequestStatus,
             `appointment_request`.`is_with_assigned_cm` as isWithAssignedCm,
              `appointment_request`.`duration_in_minutes` as duration,
              `appointment_request`.`reschedule_pending` as reschedulePending,
              `appointment_request`.`location` as location,
              `appointment_request`.`address` as address,
              `appointment_request`.`video_meeting_id` as videoMeetingId,
              P.duration_in_minutes as parentDuration,
            PT.timeslot as parentTimeSlot,
              `appointment_request`.`accept_dtm` as acceptedDateTime,
               `appointment_request`.`parent_appt_request_id` as parentApptRequestId,
               participant_plan_item_id as taskId,
            appointment_request_timeslot.timeslot_id as timeSlotId,
            appointment_request_timeslot.timeslot_preference as timeSlotPreference,
            appointment_request_timeslot.timeslot as timeSlot,
            appointment_request.service_id as serviceId,
            organization_service.service_name as serviceName,
            appointment_request.appointment_batch_id as appointmentBatchId,
            appointment_settings.hide_participants_mode as hideParticipantsMode
			FROM `appointment_request` 
            LEFT JOIN `appointment_request` P on
            `appointment_request`.`parent_appt_request_id` = P.appointment_request_id
            INNER JOIN pokket_user A on
            `appointment_request`.`from_pokket_user_id`=A.pokket_user_id
            LEFT JOIN pokket_user B on
            `appointment_request`.`to_pokket_user_id`=B.pokket_user_id
            INNER JOIN pokket_user F on
            appointment_request.created_by=F.pokket_user_id
            inner join appointment_request_timeslot
            on `appointment_request`.`appointment_request_id`=`appointment_request_timeslot`.`appointment_request_id`
            left join appointment_request_timeslot PT
            on `appointment_request`.`parent_appt_request_id`=PT.`appointment_request_id`
            left join organization_service on
            appointment_request.service_id=organization_service.organization_service_id
            left join participant_plan_item
             on appointment_request.appointment_request_id=participant_plan_item.appointment_request_id
