     GROUP_CONCAT(`pokket_user_role`.`user_role_id`) AS roles,
	`pokket_user_organization`.`isAdmin` as isAdmin,
    ifnull(countOfAssignedParticipants,0) as countOfAssignedParticipants,
    IFNULL(countOfAssignedParticipantServices,0) as countOfAssignedParticipantServices,
    FSM.allSupportedServiceIds
	FROM `pokket_user`
    inner join `pokket_user_organization` on
    `pokket_user`.`pokket_user_id`=`pokket_user_organization`.`pokket_user_id` 
	inner join `pokket_user_role` 
	on `pokket_user`.`pokket_user_id`=`pokket_user_role`.`pokket_user_id`
    left join ( SELECT facility_service_case_manager.pokket_user_id, group_concat(distinct `organization_service`.`organization_service_id`) as allSupportedServiceIds
    from `organization_service` inner join facility_service_case_manager
    on `organization_service`.`organization_service_id`= facility_service_case_manager.service_id
    where `organization_service`.`service_status_id` !=5 and facility_service_case_manager.is_active=1
    group by facility_service_case_manager.pokket_user_id) FSM
    on `pokket_user`.`pokket_user_id`= FSM.pokket_user_id
    left join (select count(*) as countOfAssignedParticipants, pokket_user_id from
    pokket_user_connection where is_active=1  
    and connection_pokket_user_role_id=5
    group by pokket_user_id) A
    on `pokket_user`.`pokket_user_id`=A.`pokket_user_id` 
    left join (
    select count(*) as countOfAssignedParticipantServices, case_manager_id from 
    participant_referral inner join pokket_user on
    participant_referral.pokket_user_id= pokket_user.pokket_user_id 
    inner join organization_service on
    participant_referral.service_id=organization_service.organization_service_id
    where is_service_assigned_to_participant=1
    and pokket_user.pokket_user_status_id=4 and
    organization_service.service_status_id=2 
    group by case_manager_id
    ) B
    on `pokket_user`.`pokket_user_id`=B.`case_manager_id` 
    where `pokket_user`.`pokket_user_status_id`= 4 AND
    `pokket_user_organization`.`organization_id`=organizationId AND
    `pokket_user_role`.`user_role_id`=4
    GROUP BY `pokket_user_role`.`pokket_user_id`
    ORDER BY `pokket_user`.`last_name`;


END ;;
