    and `pokket_user_connection`.`connection_pokket_user_role_id`=4
	AND `pokket_user_connection`.`is_active`=1 )
    GROUP BY `pokket_user_role`.`pokket_user_id`
    ORDER BY `pokket_user`.`last_name`;

    

     
     
     


    
    
    

    

END ;;
