CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetCaseManagersForService`(
		serviceId varchar(50),awsRequestId varchar(50))
BEGIN
    
    
    
    SELECT  
    `pokket_user`.`pokket_user_id` as pokketUserId,
    `pokket_user`.`email_address` as emailAddress,
    `pokket_user`.`pokket_user_status_id` as status,
    `pokket_user`.`name_prefix` as namePrefix,
    `pokket_user`.`first_name` as firstName,
    `pokket_user`.`middle_name` as middleName,
    `pokket_user`.`last_name` as lastName,
    `pokket_user`.`name_suffix` as nameSuffix,
    `pokket_user`.`preferred_name` as preferredName,
    `pokket_user`.`profile_image_url` as profileImageUrl,
    `pokket_user`.`primary_phone_number` as primaryPhoneNumber,
    `pokket_user`.`secondary_phone_number` as secondaryPhoneNumber,
     GROUP_CONCAT(`pokket_user_role`.`user_role_id`) AS roles,
	`pokket_user_organization`.`isAdmin` as isAdmin,
    ifnull(countOfAssignedParticipants,0) as countOfAssignedParticipants
	FROM `pokket_user`
    inner join `pokket_user_organization` on
    `pokket_user`.`pokket_user_id`=`pokket_user_organization`.`pokket_user_id` 
	inner join `pokket_user_role` 
	on `pokket_user`.`pokket_user_id`=`pokket_user_role`.`pokket_user_id`
    left join (select count(*) as countOfAssignedParticipants, pokket_user_id from
    pokket_user_connection where is_active=1  
    and connection_pokket_user_role_id=5
    group by pokket_user_id) A
    on `pokket_user`.`pokket_user_id`=A.`pokket_user_id` 
    where `pokket_user`.`pokket_user_status_id`= 4  and 
    `pokket_user`.`pokket_user_id` IN
    (SELECT `facility_service_case_manager`.`pokket_user_id`
 	FROM `facility_service_case_manager` inner join
    `facility_service` on 
    `facility_service_case_manager`.`facility_id`=`facility_service`.`facility_id`
    and `facility_service_case_manager`.`service_id`=`facility_service`.`service_id`
	WHERE `facility_service_case_manager`.`service_id`=serviceId
	AND `facility_service_case_manager`.`is_active`=1)
    GROUP BY `pokket_user_role`.`pokket_user_id`
    ORDER BY `pokket_user`.`last_name`;

    

     
     
     


    
    
    

    

END ;;
