CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetCheckinShares`(
		checkInId varchar(50),
        pokketUserId varchar(50),
        awsRequestId varchar(50))
BEGIN
					
	select participant_plan_item_id, count(*) into @planItemId,
	@countOCheckins from participant_check_in where
	participant_check_in_id=checkInId;
    

	
    set @sharedBy = pokketUserId;
	select parent_plan_item_id into @parentPlanItem from participant_plan_item where
		participant_plan_item_id=@planItemId;
		
	select parent_plan_item_id into @parentParentPlanItem from participant_plan_item where
		participant_plan_item_id=@parentPlanItem;
                   
	if @planItemId is not null then
		select participant_plan_item.pokket_user_id,participant_plan_item.need_category_id into @participantPokketUserId,
		@needCategoryId from participant_plan_item 
		inner join participant_check_in on
		participant_plan_item.participant_plan_item_id=participant_check_in.participant_plan_item_id
		where participant_plan_item.participant_plan_item_id=@planItemId
		and participant_check_in_id=checkInId;
	else
		select pokket_user_id,need_category_id into @participantPokketUserId,
		@needCategoryId from participant_check_in
		where pokket_user_id=pokketUserId and participant_check_in_id=checkInId; 
	end if;
    
	select pokket_user_id into @participantPokketUserId
	from participant_check_in where
	participant_check_in_id=checkInId;
    

                
	if @participantPokketUserId=pokketUserId then
	
					
		select count(*) into @countOfActivePlanItems from (
		select participant_plan_item_id from participant_plan_item where 	
		participant_plan_item_id=@planItemId and status_id !=4
		union
		select plan_item_id from plan_item_recurrence where
		plan_item_id=@planItemId and status_id !=4) A;
        
	select parent_plan_item_id into @parentParentPlanItem from participant_plan_item where
		participant_plan_item_id=@parentPlanItem;
		
		select count(*) into @countOfActiveWholePlanShares from vw_active_pokket_user_shares 
			where share_type_id=1 and shared_by=@sharedBy;
            
			select UNIX_TIMESTAMP(max(ifnull(modified_dtm,created_dtm)))*1000 into @wholePlanShareCreatedTime from vw_active_pokket_user_shares 
			where share_type_id=1 and shared_by=@sharedBy;
    
    				

		
	
		

     					
		if @countOfActivePlanItems >0  or @countOfActiveWholePlanShares > 0 then
        
