CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetFacilitiesForService`(
		serviceId varchar(50),awsRequestId varchar(50))
BEGIN
	SELECT `organization_facility`.`organization_facility_id` as facilityId,
    `organization_facility`.`facility_name` as name,
    `organization_facility`.`facility_status_id` as status,
    if(`organization_facility`.`is_virtual` = 1,true,false) as facilityIsVirtual
    from `organization_facility` 
    where `organization_facility`.`organization_facility_id`
    in (SELECT `facility_service`.`facility_id`FROM `facility_service`
		WHERE `service_id`=serviceId);
        


    

END ;;
