CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetFacility`(
		facilityId varchar(50),awsRequestId varchar(50))
BEGIN
    
    
    SELECT `organization_facility`.`organization_facility_id` as facilityId,
    `organization_facility`.`organization_id` as orgId,
    `organization_facility`.`facility_name` as name,
    `organization_facility`.`facility_status_id` as status,
    `organization_facility`.`facility_operating_hours` as operatingHours,
    `organization_facility`.`address_line_1` as addressLine1,
    `organization_facility`.`address_line_2` as addressLine2,
    `organization_facility`.`address_city` as city,
    `organization_facility`.`address_state` as state,
    `organization_facility`.`address_county` as county,
    `organization_facility`.`address_zip` as zip,
    `organization_facility`.`phone_number` as phoneNumber,
    `organization_facility`.`facility_latitude` as lat,
    `organization_facility`.`facility_longitude` as lng,
    GROUP_CONCAT(`A`.`organization_service_id`) AS serviceIds,
    GROUP_CONCAT(`A`.`service_name`) AS serviceNames,
    GROUP_CONCAT(`A`.`need_category_id`) AS needCategoryIds,
    if(`organization_facility`.`is_virtual` = 1,true,false) as facilityIsVirtual
  	FROM `organization_facility` left join
    (select `organization_service_id`,`service_name`,`need_category_id` ,
    `facility_id`
    from `organization_service` inner join 
    `facility_service` on `organization_service`.`organization_service_id`=
    `facility_service`.`service_id` where `facility_service`.`facility_id`=
    facilityId) `A`
    on `organization_facility`.`organization_facility_id`=`A`.`facility_id`
    where `organization_facility`.`organization_facility_id`=facilityId;

    

END ;;
