 and `organization_service`.`veteran_only` <= @participant_is_veteran
 and FIND_IN_SET(`organization_service`.`organization_service_id`, allServices)
 ) A
 where facilityDistance <=radius
 or serviceArea = 'NATIONWIDE'
 or (serviceArea = 'STATEWIDE' and INSTR(serviceStates,searchState)>0)
ORDER BY isSpaAssigned DESC, distance ASC, orgName ASC;

