CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetMessageThread`(
		messageThreadId varchar(50), viewerPokketUserId varchar(50), awsRequestId varchar(50))
BEGIN


select count(*), guardian_dependent.dependent_id into @viewerIsGuardian, @dependentUserId
	from guardian_dependent 
    join pokket_user_message_thread ON pokket_user_message_thread.pokket_user_id = guardian_dependent.dependent_id
	where pokket_user_message_thread.message_thread_id = messageThreadId 
    and guardian_dependent.guardian_id = viewerPokketUserId 
    and guardian_dependent.is_active = 1;
    
if @viewerIsGuardian > 0 AND @dependentUserId is not null then
	set viewerPokketUserId = @dependentUserId;
 end if;

select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=viewerPokketUserId 
		group by pokket_user_id) AS A;
    
SELECT distinct `message_thread`.`message_thread_id` as messageThreadId,
    `message_thread`.`message_thread_title` as messageThreadTitle,
    L.isLocked,
    pokket_users as pokketUsers , C.message_text as lastMessageText,
	DATE_FORMAT(C.lastMessageCreatedDateTime,'%m/%d/%Y %r') as lastMessageCreatedDateTime,
    C.pokket_user_id as lastMessageFromPokketUserId,
    C.first_name as lastMessageFromFirstName,
    C.last_name as lastMessageFromLastName,
    C.profile_image_url as lastMessageFromProfileImageUrl,
    is_archived as isArchived
	FROM `message_thread` 
		inner join pokket_user_message_thread on
		message_thread.message_thread_id=pokket_user_message_thread.message_thread_id 
		inner join
		(select  A.message_thread_id, A.pokket_user_id, if(totalParticipation - ifnull(totalLockedParticipation,0)=0,1,0) as isLocked from
		(select message_thread_id, pokket_user_id, count(*) as totalParticipation from pokket_user_message_thread
		group by message_thread_id, pokket_user_id) A
		left join 
		(select message_thread_id, pokket_user_id, count(*) as totalLockedParticipation from pokket_user_message_thread
		where is_locked =1
		group by message_thread_id, pokket_user_id) B
		on A.message_thread_id=B.message_thread_id
		and A.pokket_user_id=B.pokket_user_id
		where A.pokket_user_id=viewerPokketUserId) L
		on `message_thread`.`message_thread_id` = L.message_thread_id
		inner join 
		(select message_thread_id,GROUP_CONCAT(pokket_users SEPARATOR '|') as pokket_users from
		(SELECT  message_thread_id, 
		CONCAT(pokket_user_message_thread.pokket_user_id,'^',
		 first_name,'^',last_name,'^',ifnull(profile_image_url,''),'^',ifnull(pokket_user_message_thread.service_id,''),'^',ifnull(organization_service.service_name,''),'^',pokket_user_message_thread.is_locked,'^',if(isnull(participant_id)=1,0,5))  as pokket_users
		 FROM pokket_user_message_thread
		inner join pokket_user on
		pokket_user.pokket_user_id=pokket_user_message_thread.pokket_user_id
		left join participant on pokket_user.pokket_user_id = participant.pokket_user_id
		left join organization_service on
		pokket_user_message_thread.service_id=organization_service.organization_service_id
		where pokket_user_message_thread.is_active=1) A
		GROUP BY message_thread_id) B
		on `message_thread`.`message_thread_id`=B.message_thread_id
		left join (
		select message_text,message_thread_id,message.created_dtm as lastMessageCreatedDateTime,
		pokket_user.pokket_user_id,first_name,last_name,profile_image_url
		from message inner join pokket_user_message
		on message.message_id=pokket_user_message.message_id
		inner join pokket_user on 
		pokket_user_message.pokket_user_id=pokket_user.pokket_user_id
		where message.message_id in (select mypokket.getLastMessageId(messageThreadId, viewerPokketUserId))
		and pokket_user_message.pokket_user_id=viewerPokketUserId
		) C 
		on message_thread.message_thread_id=C.message_thread_id 
		where `pokket_user_message_thread`.`message_thread_id`=messageThreadId
		and (pokket_user_message_thread.pokket_user_id=viewerPokketUserId
		or find_in_set( pokket_user_message_thread.service_id, @serviceIds));
    
END ;;
