CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetOrganization`(
		pokketUserId varchar(50),viewerPokketUserId varchar(50), awsRequestId varchar(50))
BEGIN
	
	select organization_id into @organizationId from pokket_user_organization 
		where pokket_user_id=pokketUserId;
    
    select lft,rgt into @lft,@rgt from organization where organization_id=@organizationId;    	    
    
    select organization_id into @viewerOrgId from pokket_user_organization
		where pokket_user_id=viewerPokketUserId;    
    
    select contract_id into @contractId from organization where organization_id=@viewerOrgId;

	select owners INTO @owners from (
		select @organizationId as node_org, group_concat(pokket_user.pokket_user_id) as owners from 
		pokket_user inner join
		pokket_user_organization on
		pokket_user.pokket_user_id=pokket_user_organization.pokket_user_id
		inner join 
		pokket_user_role on 
		pokket_user_organization.pokket_user_id = pokket_user_role.pokket_user_id
		where pokket_user.pokket_user_status_id=4 and
		pokket_user_role.user_role_id=3
		and pokket_user_organization.organization_id in (
		SELECT parent.organization_id
		FROM organization AS node,
				organization AS parent
		WHERE node.lft BETWEEN parent.lft AND parent.rgt
				AND node.organization_id = @organizationId
				and parent.organization_id != @organizationId
		ORDER BY parent.lft)
		group by node_org) A;
    	
      set @leadAgencyOrgId=0;

	  select is_lead_agency into @isLeadAgency from organization where organization_id=@organizationId;
        
	if @isLeadAgency=1 then
			set @leadAgencyOrgId=@organizationId;
	else    
		SELECT parent.organization_id into @leadAgencyOrgId
		FROM organization AS node,
				organization AS parent
		WHERE node.lft BETWEEN parent.lft AND parent.rgt
				AND node.organization_id = @organizationId and parent.is_lead_agency=1
		ORDER BY parent.lft desc limit 1;
	end if;
    
    select  if(count(*)>0,1,0) into @hasVideoContract from organization_role where organization_id=@leadAgencyOrgId AND org_role_id=8 AND is_active=1;
    
    select  if(count(*)>0,1,0) into @hasServices from vw_active_organization_services where organization_id=@organizationId;
   	
   	select  count(*)  into @servicesCount from vw_active_organization_services where organization_id=@organizationId;
    
    select  if(count(*)>0,1,0) into @hasFacilites from vw_active_organization_facilities where organization_id=@organizationId;
   
  	select  count(*)  into @facilitiesCount from vw_active_organization_facilities where organization_id=@organizationId AND facility_name != "VIRTUAL";
    
    select if(count(*)>0,1,0) into @hasServicesWithFacilities from vw_active_organization_services
		inner join facility_service 
		on vw_active_organization_services.organization_service_id=facility_service.service_id
		inner join vw_active_organization_facilities 
		on facility_service.facility_id=vw_active_organization_facilities.organization_facility_id
		where vw_active_organization_services.organization_id=@organizationId;
    
    select if(count(*)>0,1,0) into @hasServicesWithCMs from 
		vw_active_organization_services inner join facility_service_case_manager 
		on vw_active_organization_services.organization_service_id= facility_service_case_manager.service_id
		inner join vw_active_pokket_users 
		on facility_service_case_manager.pokket_user_id = vw_active_pokket_users.pokket_user_id
		where facility_service_case_manager.is_active=1 and vw_active_organization_services.organization_id=@organizationId;
    
	select if(count(*)>0,1,0) into @hasFacilitiesWithCMs from 
		vw_active_organization_facilities inner join facility_case_manager 
		on vw_active_organization_facilities.organization_facility_id= facility_case_manager.facility_id
		inner join vw_active_pokket_users 
		on facility_case_manager.pokket_user_id = vw_active_pokket_users.pokket_user_id
		where facility_case_manager.is_active=1 and vw_active_organization_facilities.organization_id=@organizationId;
 
	SELECT `organization`.`organization_id` as organizationId,
		`organization`.`organization_name` as name,
		`organization`.`organization_description` as description,
		`organization`.`organization_type_id`as orgType,
		`organization`.`organization_status_id` as status,
		`organization`.`jurisdiction_code` as jurisdictionCode,
		`organization`.`contract_id` as contractId,
		`organization`.`is_lead_agency` as isLeadAgency,
		`organization`.`is_cja` as isCriminalJusticeAgency,    
		`organization`.`is_non_participating` as isNonParticipating,
		 `organization_onboarding`.`sp_invite_status_id` as spInviteStatus,
		 `organization_onboarding`.`onboarded_by_org_id` as onboardedByOrgId,
		 `organization`.`pro_user_delivery_channel_pref` as proUserDeliveryChannelPreference,
		`organization`.`organization_website` as website,
		 @leadAgencyOrgId as leadAgencyOrgId,
		 @hasVideoContract as hasVideoContract,
		 GROUP_CONCAT(`organization_role`.`org_role_id`) AS roles,
		 @owners as ownerSpas,
		 @hasServices as hasServices,
		 @hasFacilites as hasFacilites,
		 @hasServicesWithFacilities as hasServicesWithFacilities,
		 @hasServicesWithCMs as hasServicesWithCMs,
		 @hasFacilitiesWithCMs as hasFacilitiesWithCMs,
		 spaCount, 
         cmCount,
         participantCount,
         spaCmCount,
         @servicesCount as servicesCount,
         @facilitiesCount as facilitiesCount,
         serviceProvidersCount, 
		 activeSpaCount,
         activeCmCount,
         activeSpaCmCount,
         guardianCount
		 FROM `organization` left join 
		 `organization_onboarding` on
		 `organization`.`organization_id`=
		 `organization_onboarding`.`organization_id` left join 
		 `organization_role` on
