select @organizationId as node_org, group_concat(pokket_user.pokket_user_id) as owners from 
pokket_user inner join
pokket_user_organization on
pokket_user.pokket_user_id=pokket_user_organization.pokket_user_id
inner join 
pokket_user_role on 
pokket_user_organization.pokket_user_id = pokket_user_role.pokket_user_id
where pokket_user.pokket_user_status_id=4 and
pokket_user_role.user_role_id=3
and pokket_user_organization.organization_id in (
SELECT parent.organization_id
FROM organization AS node,
        organization AS parent
WHERE node.lft BETWEEN parent.lft AND parent.rgt
        AND node.organization_id = @organizationId
        and parent.organization_id != @organizationId
ORDER BY parent.lft)
group by node_org) A; 

select organization_id into @onboardedByOrgId from pokket_user_organization 
inner join pokket_user on
pokket_user_organization.pokket_user_id=pokket_user.created_by
where
pokket_user.pokket_user_id=pokketUserId;

select count(*)  into @countOfPhoneNumberChanges 
from pokket_user_history where pokket_user_id=pokketUserId and
old_primary_phone_number <> new_primary_phone_number;

if @countOfPhoneNumberChanges = 0 then
	select first_name , last_name, primary_phone_number, user_name into 
	@createdByFirstName, @createdByLastName, @createdByPhoneNumber, @createdByUserName
	from pokket_user where pokket_user_id in (select created_by from pokket_user 
	where pokket_user_id=pokketUserId);
 else
	 select modified_by into @modifiedBy  from pokket_user_history where pokket_user_id=pokketUserId and
	old_primary_phone_number <> new_primary_phone_number order by version desc limit 1;
	select first_name , last_name, primary_phone_number,user_name into 
	@createdByFirstName, @createdByLastName, @createdByPhoneNumber,@createdByUserName
	from pokket_user where pokket_user_id =@modifiedBy;
end if;

 SELECT `pokket_user`.`is_cm_using_spa_phone` into @isNotOwnPhone from pokket_user where pokket_user_id=pokketUserId; 
 
 select count(*) into @countOfParticipant from participant where pokket_user_id=pokketUserId; 
 if @countOfParticipant != 0 then
	select locate('+',primary_phone_number) into @isValidPhone from pokket_user where pokket_user_id=pokketUserId;
	select in_custody_status into @inCustodyStatus from participant  where pokket_user_id=pokketUserId;
