    IF(TIMESTAMPDIFF(SECOND, DATE_FORMAT(NOW(),"%Y-%m-%d 00:00:00"),IFNULL(participant.task_counts_updated_dtm,'1900-01-01 00:00:00')) <0,1,0) as areCountsStale,
    countOfUnreadNotifications,countOfUnreadMessages
	FROM `participant` inner join `pokket_user`
    on `participant`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
    inner join (select pokket_user.pokket_user_id,ifnull(CountOfUnreadNotifications,0) as countOfUnreadNotifications,ifnull(CountOfUnreadMessages,0) as countOfUnreadMessages  from pokket_user 
left join 
(select pokket_user_id,count(*) as CountOfUnreadNotifications from notification
where is_read=0

group by pokket_user_id) A
on pokket_user.pokket_user_id=A.pokket_user_id
left join
(select pokket_user_id,count(*) as CountOfUnreadMessages from pokket_user_message
where is_read=0

group by pokket_user_id) B
on pokket_user.pokket_user_id=B.pokket_user_id
where pokket_user.pokket_user_id=pokketUserId) A
on pokket_user.pokket_user_id = A.pokket_user_id 
    inner join `pokket_user_role` 
	on `pokket_user`.`pokket_user_id`=`pokket_user_role`.`pokket_user_id`
    WHERE `participant`.`pokket_user_id`=pokketUserId
    GROUP BY `pokket_user_role`.`pokket_user_id`;

END ;;
