    `participant_check_in`.`lat` as latitude,
    `participant_check_in`.`lon` as longitude,
    `participant_check_in`.`lat_lon_accuracy` as accuracy,
	`participant_check_in`.`selfie_url` as selfieUrl,    
    `participant_check_in`.`derived_address` as derivedAddress,
	`participant_check_in`.`location_lat` as locationLat,
	`participant_check_in`.`location_lon` as locationLon,
    `participant_check_in`.`need_category_id` as needCategory,',
    QUOTE(pokketUserId),' as pokketUserId,
    DATE_FORMAT(`participant_check_in`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as checkInDateTime,
    `participant_check_in`.`created_dtm`
	FROM `participant_check_in` 
	WHERE `participant_check_in`.`need_category_id` in ( select need_category_id  from vw_active_pokket_user_shares 
            where share_type_id=2 and shared_by=', QUOTE(pokketUserId), ' and  FIND_IN_SET(shared_with,', QUOTE(@serviceIds), ')) 
	 and 
    `participant_check_in`.`created_dtm` between DATE_SUB(participant_check_in.created_dtm,INTERVAL 6 month) and CURRENT_TIMESTAMP
    and `participant_check_in`.`pokket_user_id`=',QUOTE(pokketUserId));
    IF needCategoryId IS NOT NULL THEN
    SET @Query = CONCAT(@Query, ' AND
    `participant_check_in`.`need_category_id`=',QUOTE(needCategoryId));
	END IF;
    SET @Query = CONCAT(@Query, ' UNION  ',
    'SELECT `participant_check_in`.`participant_check_in_id` as checkInId,
    `participant_check_in`.`participant_plan_item_id` as planItemId,
    `participant_check_in`.`recurrence_id` as recurrenceId,
    `participant_check_in`.`check_in_notes` as `notes`,
    `participant_check_in`.`is_checkout` as `isCheckout`,
    `participant_check_in`.`lat` as latitude,
    `participant_check_in`.`lon` as longitude,
    `participant_check_in`.`lat_lon_accuracy` as accuracy,
	`participant_check_in`.`selfie_url` as selfieUrl,    
    `participant_check_in`.`derived_address` as derivedAddress,
	`participant_check_in`.`location_lat` as locationLat,
	`participant_check_in`.`location_lon` as locationLon,
    `participant_check_in`.`need_category_id` as needCategory,',
    QUOTE(pokketUserId),' as pokketUserId,
    DATE_FORMAT(`participant_check_in`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as checkInDateTime,
        `participant_check_in`.`created_dtm`
	FROM `participant_check_in` 
	WHERE `participant_check_in`.`need_category_id` in ( select  need_category_id  from ref_need_category) 
		 and ', @countOfActiveWholePlanShares, ' > 0  
	 and 
    `participant_check_in`.`created_dtm` between DATE_SUB(participant_check_in.created_dtm,INTERVAL 6 month) and CURRENT_TIMESTAMP
    and `participant_check_in`.`pokket_user_id`=',QUOTE(pokketUserId)); 
    IF needCategoryId IS NOT NULL THEN
    SET @Query = CONCAT(@Query, ' AND
    `participant_check_in`.`need_category_id`=',QUOTE(needCategoryId));
	END IF;
        SET @Query = CONCAT(@Query, ' UNION  ',
    'SELECT `participant_check_in`.`participant_check_in_id` as checkInId,
    `participant_check_in`.`participant_plan_item_id` as planItemId,
    `participant_check_in`.`recurrence_id` as recurrenceId,
    `participant_check_in`.`check_in_notes` as `notes`,
    `participant_check_in`.`is_checkout` as `isCheckout`,
    `participant_check_in`.`lat` as latitude,
    `participant_check_in`.`lon` as longitude,
    `participant_check_in`.`lat_lon_accuracy` as accuracy,
	`participant_check_in`.`selfie_url` as selfieUrl,    
    `participant_check_in`.`derived_address` as derivedAddress,
	`participant_check_in`.`location_lat` as locationLat,
	`participant_check_in`.`location_lon` as locationLon,
    `participant_check_in`.`need_category_id` as needCategory,',
    QUOTE(pokketUserId),' as pokketUserId,
    DATE_FORMAT(`participant_check_in`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as checkInDateTime,
        `participant_check_in`.`created_dtm`
	FROM `participant_check_in` inner join vw_active_pokket_user_shares 
    on participant_check_in.participant_check_in_id = vw_active_pokket_user_shares.shared_ref_id
	WHERE `vw_active_pokket_user_shares`.`share_type_id` = 6  
    and shared_by=', QUOTE(pokketUserId), ' and  FIND_IN_SET(shared_with,', QUOTE(@serviceIds), ') and 
    `participant_check_in`.`created_dtm` between DATE_SUB(participant_check_in.created_dtm,INTERVAL 6 month) and CURRENT_TIMESTAMP
    and `participant_check_in`.`pokket_user_id`=',QUOTE(pokketUserId));
    SET @Query = CONCAT(@Query, ') A where checkInId not in 
    ( select shared_ref_id from pokket_user_share where shared_by=', QUOTE(pokketUserId), 'and  share_type_id=6 and is_active=0
		and shared_with in (
        select service_id from facility_service_case_manager where pokket_user_id=', QUOTE(sharedWithPokketUserId), ' and is_active=1
        )
    ) 
    order by created_dtm desc');

    PREPARE stmt FROM @Query;
    EXECUTE stmt;
 
  end if;
END ;;
