CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetParticipantPlanItem`(
		planItemId varchar(50),recurrenceId varchar(50),
        viewerPokketUserId varchar(50),awsRequestId varchar(50))
BEGIN

SET @grandparentPlanItemId ='';

if recurrenceId = '0' or recurrenceId is null then
	select count(*) into @isDeleted from participant_plan_item where participant_plan_item_id=planItemId and
	status_id=4;
else
	select count(*) into @isDeleted from plan_item_recurrence where plan_item_id=planItemId and
	recurrence_id=recurrenceId and status_id=4;
end if;

if @isDeleted =0 then

	select group_concat(service_id) into @serviceIds from facility_service_case_manager where pokket_user_id= viewerPokketUserId
	and is_active=1
	group by pokket_user_id;

	select organization_id into @viewerOrganizationId from vw_pokket_user_organization where pokket_user_id=viewerPokketUserId;

	select appointment_request.video_meeting_id, appointment_request.appointment_batch_id 
		into @videoMeetingId, @appointmentBatchId 
        from participant_plan_item 
		JOIN appointment_request 
		ON participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
		WHERE participant_plan_item.participant_plan_item_id=planItemId;

    select count(*) into @numberOfDocuments from plan_item_document
		where participant_plan_item_id=planItemId;

	select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=viewerPokketUserId 
		group by pokket_user_id) AS A;

	select count(*) into @countOfActivePlanItems from (
		select participant_plan_item_id from participant_plan_item where 	
		participant_plan_item_id=planItemId and status_id !=4
		union
		select plan_item_id from plan_item_recurrence where
		plan_item_id=planItemId and status_id !=4) A;

    select `participant_plan_item`.`pokket_user_id`,
		`participant_plan_item`.`parent_plan_item_id` , `participant_plan_item`.`need_category_id` 
		into @participantPokketUserId,@parentPlanItemId ,@planItemNeedCategoryId
		FROM `participant_plan_item` 
		where  `participant_plan_item`.`participant_plan_item_id`=planItemId ;
    
    if @parentPlanItemId is not null then
		SELECT parent_plan_item_id into @parentParentPlanItemId FROM participant_plan_item where participant_plan_item_id = @parentPlanItemId;
		if @parentParentPlanItemId is not null then
			set @grandparentPlanItemId = @parentParentPlanItemId;
		end if;
    end if;

    select participant_plan_item_description into 
		@parentPlanItemDescription from participant_plan_item
		where participant_plan_item_id=@parentPlanItemId;
	
    select parent_plan_item_id into @parentPlanItem from participant_plan_item where
				participant_plan_item_id=planItemId;
                
	select parent_plan_item_id into @parentParentPlanItem from participant_plan_item where
				participant_plan_item_id=@parentPlanItem;
                
	set @canViewPlanItem = (viewerPokketUserId=@participantPokketUserId );
      
	IF (viewerPokketUserId != @participantPokketUserId ) THEN
		
        select count(*) into @viewerIsGuardian from guardian_dependent where
				dependent_id = @participantPokketUserId and guardian_id = viewerPokketUserId and is_active = 1;
		
        IF @viewerIsGuardian > 0 THEN
			
            set @canViewPlanItem=true;
		ELSE
			
            
			select count(*) into @countOfDirectShares from vw_active_pokket_user_shares 
					where share_type_id=3 and shared_ref_id=planItemId and 
						shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);       
						
			select count(*) into @countOfDirectUnShares from pokket_user_share
				where share_type_id=3 and shared_ref_id=planItemId and 
				shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds)
				and is_active=0;

			select count(*) into @countOfWholePlanShares from vw_active_pokket_user_shares 
				where share_type_id=1 and shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
						
			select count(*) into @countOfNeedCategoryShares from vw_active_pokket_user_shares 
				where share_type_id=2 and need_category_id=@planItemNeedCategoryId and  
				 shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);

			select count(*) into @countOfDirectParentShares from vw_active_pokket_user_shares 
					where share_type_id=3 and shared_ref_id=@parentPlanItem and 
						shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
						
			select count(*) into @countOfDirectGrandparentShares from vw_active_pokket_user_shares 
				where share_type_id=3 and shared_ref_id=@parentParentPlanItem and 
				shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
			
			
			
			select count(*) into @countOfDirectOwnership from participant_plan_item
				where participant_plan_item.participant_plan_item_id = planItemId
				and participant_plan_item.created_by = viewerPokketUserId;
				
			select count(*) into @countOfIndirectOwnership from participant_plan_item 
				inner join facility_service_case_manager on participant_plan_item.service_id = facility_service_case_manager.service_id
				where participant_plan_item.participant_plan_item_id = planItemId
				and facility_service_case_manager.pokket_user_id = viewerPokketUserId
				and facility_service_case_manager.is_active = 1;
						
			if @countOfDirectUnShares > 0 then
				set @canViewPlanItem=false;
			else
				set @canViewPlanItem= (@countOfDirectShares + 
													 @countOfWholePlanShares + @countOfNeedCategoryShares + 
													 @countOfDirectParentShares + @countOfDirectGrandparentShares) > 0;
			END IF;
			if (@countOfDirectOwnership + @countOfIndirectOwnership) > 0 then
			
					set @canViewPlanItem=true;
			END IF;
        END IF;
			
	end if;

	select count(*) into @countOfActiveWholePlanShares from vw_active_pokket_user_shares 
            where share_type_id=1 and shared_by=@participantPokketUserId and  FIND_IN_SET(shared_with,@serviceIds);

	if @canViewPlanItem then 

		if recurrenceId = '0' or recurrenceId is null then
			SELECT `participant_plan_item`.`participant_plan_item_id` as planItemId,
			`participant_plan_item`.`participant_plan_item_description` as description,
			`participant_plan_item`.`pokket_user_id` as pokketUserId,
			`participant_plan_item`.`parent_plan_item_id` as parentPlanItemId,
			@grandparentPlanItemId as grandparentPlanItemId,
			 @parentPlanItemDescription as parentPlanItemDescription,
			`participant_plan_item`.`plan_item_type_id` as planItemType,
			 `plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
			 DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
			 IFNULL(`plan_item_recurrence`.`recurrence_id`,0) as recurrenceId,
			 @numberOfDocuments as numberOfDocuments,
			`participant_plan_item`.`location` as location,
			`participant_plan_item`.`location_lat` as locationLat,
			`participant_plan_item`.`location_lon` as locationLon,
			@videoMeetingId as videoMeetingId,
            @appointmentBatchId as appointmentBatchId,
			`participant_plan_item`.`due_date` as dueDate,
			`participant_plan_item`.`start_time` as startTime,
			`participant_plan_item`.`completion_date` as completionDate,
			`participant_plan_item`.`duration_in_minutes` as duration,
			`participant_plan_item`.`status_id` as `status`,
			`participant_plan_item`.`notes` as `notes`,
			`participant_plan_item`.`need_category_id` as needCategory,
			`participant_plan_item`.`is_need_met_after_completion` as didPlanCompletionMeetNeed,
			`participant_plan_item`.`completion_notes` as completionNotes,
			`participant_plan_item`.`repeat_interval` as repeatInterval,
			`participant_plan_item`.`repeat_type_id` as repeatType,
			`participant_plan_item`.`service_plan_item_id` as servicePlanItemId,
			`participant_plan_item`.`appointment_request_id` as appointmentRequestId,
			`participant_plan_item`.`severity_id` as skipSeverityId,
			`participant_plan_item`.`priority` as priority,
			`participant_plan_item`.`action_type_id` as planItemActionType,
			`participant_plan_item`.`created_by` as createdByPokketUserId,
			Q.hasUncompletedItems,
			Q.parentHasUncompletedItems,
			Q.grandparentHasUncompletedItems,
			`pokket_user`.`first_name` as createdByPokketUserFirstName,
			`pokket_user`.`last_name` as  createdByPokketUserLastName,
			IF (viewerPokketUserId = @participantPokketUserId,null,   if(@viewerOrganizationId=vw_pokket_user_organization_professional.organization_id,1,0) ) as isCreatorOrg,
			 IF (viewerPokketUserId = @participantPokketUserId,null,   if(ifnull(find_in_set(participant_plan_item.service_id,@serviceIds),0)=0,0,1)) as isCreatorService,
			`participant_plan_item`.`connection_pokket_user_id` as connectionPokketUserId,
			 p.`first_name` as connectionFirstName,
			 p.`last_name` as  connectionLastName,
			 `participant_plan_item`.`is_mandatory` as isMandatory,
			 `participant_plan_item`.`service_id` as serviceId,
			 organization_service.service_name as serviceName,
			organization.organization_name as organizationName
			 FROM `participant_plan_item` inner join `pokket_user`
			 on `participant_plan_item`.`created_by` = `pokket_user`.`pokket_user_id`
			 INNER JOIN (select participant_plan_item.participant_plan_item_id, 
			if(A.inprogressItems = 0 OR isnull(A.inprogressItems), 0,1) as hasUncompletedItems,
			if(B.inprogressItems = 0 OR isnull(B.inprogressItems), 0,1) as parentHasUncompletedItems ,
			if(C.inprogressItems = 0 OR isnull(C.inprogressItems), 0,1) as grandparentHasUncompletedItems 
			from
			participant_plan_item left join 
			participant_plan_item P on participant_plan_item.parent_plan_item_id = P.participant_plan_item_id
			left join 
			(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
			status_id = 2 group by parent_plan_item_id) A 
