				`participant_plan_item`.`action_type_id` as planItemActionType,
				`participant_plan_item`.`created_by` as createdByPokketUserId,
				Q.hasUncompletedItems,
				Q.parentHasUncompletedItems,      
                Q.grandparentHasUncompletedItems,
				`pokket_user`.`first_name` as createdByPokketUserFirstName,
				`pokket_user`.`last_name` as  createdByPokketUserLastName,
                if(@viewerOrganizationId=vw_pokket_user_organization_professional.organization_id,1,0) as isCreatorOrg,
                 if(ifnull(find_in_set(participant_plan_item.service_id,@serviceIds),0)=0,0,1) as isCreatorService,
				`participant_plan_item`.`connection_pokket_user_id` as connectionPokketUserId,
				 p.`first_name` as connectionFirstName,
				 p.`last_name` as  connectionLastName,
                   ifnull(`vw_active_pokket_user_shares`.`is_mandatory`,0) as isMandatory,
				  '' as inActiveRecurrenceIds,
                  `participant_plan_item`.`service_id` as serviceId,
				 organization_service.service_name as serviceName,
				organization.organization_name as organizationName
				 FROM `participant_plan_item` inner join `pokket_user`
				 on `participant_plan_item`.`created_by` = `pokket_user`.`pokket_user_id`
         INNER JOIN (select participant_plan_item.participant_plan_item_id, 
					0 as hasUncompletedItems,
					0 as parentHasUncompletedItems ,
					0 as grandparentHasUncompletedItems 
					from
					participant_plan_item left join 
					participant_plan_item P on participant_plan_item.parent_plan_item_id = P.participant_plan_item_id
					left join 
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) A 
					on participant_plan_item.participant_plan_item_id = A.parent_plan_item_id
					left join 
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) B
					on participant_plan_item.parent_plan_item_id = B.parent_plan_item_id
					left join
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) C
					on P.parent_plan_item_id = C.parent_plan_item_id) Q
					on participant_plan_item.participant_plan_item_id = Q.participant_plan_item_id                 
                  left join organization_service on participant_plan_item.service_id= organization_service.organization_service_id 
                  left join organization on organization_service.organization_id = organization.organization_id
					inner join vw_pokket_user_organization_professional on 
					  `pokket_user`.`pokket_user_id` = vw_pokket_user_organization_professional.pokket_user_id
					   left join vw_active_pokket_user_shares on 
					  `participant_plan_item`.`participant_plan_item_id`= vw_active_pokket_user_shares.shared_ref_id
					  and  vw_active_pokket_user_shares.shared_with=serviceId
				 left join `pokket_user` p
				 on `participant_plan_item`.`connection_pokket_user_id` = p.`pokket_user_id`
				 left join `plan_item_recurrence_pattern` on
							 `participant_plan_item`.`recurrence_pattern_id`=
							 `plan_item_recurrence_pattern`.`recurrence_pattern_id`
				 left join participant_plan_item B on
				 participant_plan_item.parent_plan_item_id=B.participant_plan_item_id
				 WHERE 
				 `participant_plan_item`.`repeat_type_id` = 'NONE' AND
				 `participant_plan_item`.`parent_plan_item_id`=parentPlanItemId
				 AND `participant_plan_item`.`plan_item_type_id` =childPlanItemType
				 AND `participant_plan_item`.`status_id` != 4 
				 union
				  SELECT `participant_plan_item`.`participant_plan_item_id` as planItemId,
				  `participant_plan_item`.`created_dtm`,
				`participant_plan_item`.`participant_plan_item_description` as description,
				`participant_plan_item`.`pokket_user_id` as pokketUserId,
				`participant_plan_item`.`parent_plan_item_id` as parentPlanItemId,
				B.participant_plan_item_description as parentPlanItemDescription,
                 `participant_plan_item`.`is_backchannel_sharing_accepted` as isBackChannelSharingAccepted,                
				`participant_plan_item`.`plan_item_type_id` as planItemType,
				`plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
				 `plan_item_recurrence`.`recurrence_id` as recurrenceId,
				 DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
				 `plan_item_recurrence`.`is_original_plan_item` as isOriginalPlanItem,
				`plan_item_recurrence`.`location` as location,
				`plan_item_recurrence`.`due_date` as dueDate,
				`plan_item_recurrence`.`start_time` as startTime,
				`plan_item_recurrence`.`completion_date` as completionDate,
                `participant_plan_item`.`completion_notes` as completionNotes,
                `participant_plan_item`.`duration_in_minutes` as duration,
				if(`plan_item_recurrence`.`is_original_plan_item`=1 AND `plan_item_recurrence`.`status_id`=4,2, `plan_item_recurrence`.`status_id`) as `status`, 
				`plan_item_recurrence`.`notes` as `notes`,
				`participant_plan_item`.`need_category_id` as needCategory,
				`plan_item_recurrence`.`is_need_met_after_completion` as didPlanCompletionMeetNeed,
				`participant_plan_item`.`repeat_interval` as repeatInterval,
				`participant_plan_item`.`repeat_type_id` as repeatType,
				`participant_plan_item`.`service_plan_item_id` as servicePlanItemId,
                `participant_plan_item`.`appointment_request_id` as appointmentRequestId,
				`participant_plan_item`.`severity_id` as skipSeverityId,
				`participant_plan_item`.`action_type_id` as planItemActionType,
				`participant_plan_item`.`created_by` as createdByPokketUserId,
				Q.hasUncompletedItems,
				Q.parentHasUncompletedItems,  
                Q.grandparentHasUncompletedItems,
				`pokket_user`.`first_name` as createdByPokketUserFirstName,
				`pokket_user`.`last_name` as  createdByPokketUserLastName,
                if(@viewerOrganizationId=vw_pokket_user_organization_professional.organization_id,1,0) as isCreatorOrg,
				if(ifnull(find_in_set(participant_plan_item.service_id,@serviceIds),0)=0,0,1) as isCreatorService,
				`participant_plan_item`.`connection_pokket_user_id` as connectionPokketUserId,
				 p.`first_name` as connectionFirstName,
				 p.`last_name` as  connectionLastName,
                   ifnull(`vw_active_pokket_user_shares`.`is_mandatory`,0) as isMandatory,
				 inActiveRecurrenceIds,
				`participant_plan_item`.`service_id` as serviceId,
				 organization_service.service_name as serviceName,
				organization.organization_name as organizationName
				FROM `participant_plan_item` 
				 inner join `plan_item_recurrence`
					 on `participant_plan_item`.`participant_plan_item_id`
					 =`plan_item_recurrence`.`plan_item_id`
					 inner join `plan_item_recurrence_pattern` on
					 `plan_item_recurrence`.`recurrence_pattern_id`=
					 `plan_item_recurrence_pattern`.`recurrence_pattern_id`
				 inner join `pokket_user`
				 on `participant_plan_item`.`created_by` = `pokket_user`.`pokket_user_id`
         INNER JOIN (select participant_plan_item.participant_plan_item_id, 
					0 as hasUncompletedItems,
					0 as parentHasUncompletedItems ,
					0 as grandparentHasUncompletedItems 
					from
					participant_plan_item left join 
					participant_plan_item P on participant_plan_item.parent_plan_item_id = P.participant_plan_item_id
					left join 
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) A 
					on participant_plan_item.participant_plan_item_id = A.parent_plan_item_id
					left join 
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) B
					on participant_plan_item.parent_plan_item_id = B.parent_plan_item_id
					left join
					(Select count(*) as inprogressItems, parent_plan_item_id  from participant_plan_item where 
					status_id = 2 group by parent_plan_item_id) C
					on P.parent_plan_item_id = C.parent_plan_item_id) Q
					on participant_plan_item.participant_plan_item_id = Q.participant_plan_item_id                 
                  left join organization_service on participant_plan_item.service_id= organization_service.organization_service_id 
					left join organization on organization_service.organization_id = organization.organization_id
					inner join vw_pokket_user_organization_professional on 
					  `pokket_user`.`pokket_user_id` = vw_pokket_user_organization_professional.pokket_user_id
					   left join vw_active_pokket_user_shares on 
					  `participant_plan_item`.`participant_plan_item_id`= vw_active_pokket_user_shares.shared_ref_id
					  and  vw_active_pokket_user_shares.shared_with=serviceId
				 left join `pokket_user` p
				 on `participant_plan_item`.`connection_pokket_user_id` = p.`pokket_user_id`
				 left join participant_plan_item B on
				 participant_plan_item.parent_plan_item_id=B.participant_plan_item_id
				 left join (SELECT plan_item_id,group_concat(recurrence_id) as inActiveRecurrenceIds
					 FROM mypokket.plan_item_recurrence where status_id=4
						group by plan_item_id) d
					on  `participant_plan_item`.`participant_plan_item_id` = d.plan_item_id
				 WHERE 
				 `participant_plan_item`.`repeat_type_id` != 'NONE' AND
				 `participant_plan_item`.`parent_plan_item_id`=parentPlanItemId
				 AND `participant_plan_item`.`plan_item_type_id` =childPlanItemType
                 ) U
                 
                 where (planItemId not in (select shared_ref_id from pokket_user_share where is_active=0 and shared_by=pokketUserId
					and find_in_set(shared_with,@serviceIds))
                    and parentPlanItemId not in (select shared_ref_id from pokket_user_share where is_active=0 and shared_by=pokketUserId
					and find_in_set(shared_with,@serviceIds))
                    and ((@grandParentPlanItemId is null) or not (@grandParentPlanItemId in (select shared_ref_id from pokket_user_share where is_active=0 and share_type_id=3 and shared_by=pokketUserId
					and find_in_set(shared_with,@serviceIds)) and parentPlanItemId not in (select shared_ref_id from pokket_user_share where is_active=1 and share_type_id=3 and shared_by=pokketUserId
						and find_in_set(shared_with,@serviceIds)))) 
					)
                 
                 and (planItemId in (select shared_ref_id from pokket_user_share where is_active=1 and share_type_id=3 and shared_by=pokketUserId
						and find_in_set(shared_with,@serviceIds)) 
					or parentPlanItemId in (select shared_ref_id from pokket_user_share where is_active=1 and share_type_id=3 and shared_by=pokketUserId
						and find_in_set(shared_with,@serviceIds)) 
                    or @grandParentPlanItemId in (select shared_ref_id from pokket_user_share where is_active=1 and share_type_id=3 and shared_by=pokketUserId
						and find_in_set(shared_with,@serviceIds)) 
                    or @countOfActiveWholePlanShares > 0 
                    or needCategory in (select need_category_id from pokket_user_share where is_active=1 and share_type_id=2 and shared_by=pokketUserId
						and find_in_set(shared_with,@serviceIds)) 
                 )					
				 order by  
					CASE childPlanItemType WHEN 3 THEN STR_TO_DATE(dueDate,'%m/%d/%Y') END ASC,
					CASE WHEN childPlanItemType <> 3 THEN `created_dtm` END ASC;

    end if;    

END ;;
