	`pokket_user`.`last_name` as lastName,
			`pokket_user`.`pokket_user_status_id` as participantStatus,
			`participant_referral`.`facility_id` as facilityId,
	`participant_referral`.`service_id` as serviceId,
	`organization_service`.`service_name` as serviceName,
	`organization_service`.`service_status_id` as serviceStatus,
	`organization_facility`.`facility_name` as facilityName,
			`organization_facility`.`address_line_1` as addressLine1,
			`organization_facility`.`address_line_2` as addressLine2,
			`organization_facility`.`address_city` as city,
			`organization_facility`.`address_state` as state,
			`organization_facility`.`address_county` as county,
			`organization_facility`.`address_zip` as zip,
			`organization_facility`.`phone_number` as phoneNumber,
			`participant_referral`.`case_manager_id` as caseManagerId,
			`participant_referral`.`is_accepted` as isAccepted,
	`participant_referral`.`is_onboarded` as isOnboarded,
	DATE_FORMAT(`participant_referral`.`created_dtm`, '%m/%d/%Y %r') as referralDateTime,
	`participant_referral`.`responded_dtm` as respondedDateTime,
	`ref_need_category`.`need_category_id` as needCategory,
	`ref_need_category`.`need_category_description` as needCategoryDescription,
	`participant_referral`.`referral_status_id` as referralStatus
FROM
	`participant_referral`
inner join `pokket_user` on
	`participant_referral`.`pokket_user_id` = `pokket_user`.`pokket_user_id`
inner join `organization_facility`
        on
	`organization_facility`.`organization_facility_id` = `participant_referral`.`facility_id`
inner join `organization_service` on
	`organization_service`.`organization_service_id` = `participant_referral`.`service_id`
inner join `ref_need_category` on
	`organization_service`.`need_category_id` = `ref_need_category`.`need_category_id`
where 
			CASE
		WHEN optionalServiceId IS NOT NULL 
				THEN service_id = optionalServiceId
		ELSE service_id in (
		select
			organization_service_id
		from 
						organization_service
		inner join organization
						on
			organization_service.organization_id = organization.organization_id
		inner join pokket_user_organization on
						pokket_user_organization.organization_id = organization.organization_id
		where
			pokket_user_organization.pokket_user_id = serviceProviderAdminPokketUserId
					)
	END
	and `participant_referral`.`is_accepted` is null
	and 
		`participant_referral`.`is_onboarded` is null
	and `participant_referral`.`is_service_assigned_to_participant` = 0
	and `participant_referral`.`referral_status_id` = 3
ORDER BY
	CASE
		sortLatestFirst WHEN 0 THEN `participant_referral`.`created_dtm`
	END ASC,
	CASE
		sortLatestFirst WHEN 1 THEN `participant_referral`.`created_dtm`
	END DESC;
	else 
   SELECT
	`participant_referral`.`participant_referral_id` as participantReferralId,
			`participant_referral`.`pokket_user_id` as participantPokketUserId,
			`pokket_user`.`first_name` as firstName,
	`pokket_user`.`last_name` as lastName,
			`pokket_user`.`pokket_user_status_id` as participantStatus,
			`participant_referral`.`facility_id` as facilityId,
	`participant_referral`.`service_id` as serviceId,
	`organization_service`.`service_name` as serviceName,
	`organization_service`.`service_status_id` as serviceStatus,
	`organization_facility`.`facility_name` as facilityName,
			`organization_facility`.`address_line_1` as addressLine1,
			`organization_facility`.`address_line_2` as addressLine2,
			`organization_facility`.`address_city` as city,
			`organization_facility`.`address_state` as state,
			`organization_facility`.`address_county` as county,
			`organization_facility`.`address_zip` as zip,
			`organization_facility`.`phone_number` as phoneNumber,
			`participant_referral`.`case_manager_id` as caseManagerId,
	caseManager.first_name as caseManagerFirstName,
	caseManager.last_name as caseManagerLastName,
			`participant_referral`.`is_accepted` as isAccepted,
	`participant_referral`.`is_onboarded` as isOnboarded,
	DATE_FORMAT(`participant_referral`.`created_dtm`, '%m/%d/%Y %r') as referralDateTime,
	`participant_referral`.`responded_dtm` as respondedDateTime,
	`ref_need_category`.`need_category_id` as needCategory,
	`ref_need_category`.`need_category_description` as needCategoryDescription,
	`participant_referral`.`referral_status_id` as referralStatus
FROM
	`participant_referral`
inner join `pokket_user` on
	`participant_referral`.`pokket_user_id` = `pokket_user`.`pokket_user_id`
inner join `organization_facility`
        on
	`organization_facility`.`organization_facility_id` = `participant_referral`.`facility_id`
inner join `organization_service` on
	`organization_service`.`organization_service_id` = `participant_referral`.`service_id`
inner join `ref_need_category` on
	`organization_service`.`need_category_id` = `ref_need_category`.`need_category_id`
left join `pokket_user` as caseManager
        on
	caseManager.`pokket_user_id` = `participant_referral`.`case_manager_id`
where 
			CASE
		WHEN optionalServiceId IS NOT NULL 
				THEN service_id = optionalServiceId
		ELSE service_id in (
		select
			organization_service_id
		from 
						organization_service
		inner join organization
						on
			organization_service.organization_id = organization.organization_id
		inner join pokket_user_organization on
						pokket_user_organization.organization_id = organization.organization_id
		where
			pokket_user_organization.pokket_user_id = serviceProviderAdminPokketUserId
					)
	END
	and
			CASE
		WHEN ignoreOnboardedAndAssigned = 1
				THEN participant_referral.is_onboarded is null
		and not (participant_referral.is_accepted is null
			AND participant_referral.is_service_assigned_to_participant = 1)
		ELSE TRUE
	END
ORDER BY
	CASE
		sortLatestFirst WHEN 0 THEN `participant_referral`.`created_dtm`
	END ASC,
	CASE
		sortLatestFirst WHEN 1 THEN `participant_referral`.`created_dtm`
	END DESC;
end if;

END ;;
