CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetParticipantReminders`(
		pokketUserId varchar(50),viewerPokketUserId varchar(50), awsRequestId varchar(50))
BEGIN

    if (viewerPokketUserId=pokketUserId) then 
	SELECT * FROM (
    SELECT `participant_plan_item`.`participant_plan_item_id` as reminderId,
    `participant_plan_item`.`participant_plan_item_description` as description,
    `participant_plan_item`.`pokket_user_id` as pokketUserId,
    `plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
    0 as recurrenceId,
	 DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
     1 as isOriginalPlanItem,
    `participant_plan_item`.`location` as location,
    `participant_plan_item`.`due_date` as dueDate,
    `participant_plan_item`.`completion_date` as completionDate,
	`participant_plan_item`.`completion_notes` as completionNotes,
    `participant_plan_item`.`duration_in_minutes` as duration,
    `participant_plan_item`.`status_id`  as `status`,
    `participant_plan_item`.`notes` as `notes`,
    `participant_plan_item`.`repeat_interval` as repeatInterval,
    `participant_plan_item`.`repeat_type_id` as repeatType,
     '' as inActiveRecurrenceIds
     FROM `participant_plan_item`
     left join `plan_item_recurrence_pattern` on
				 `participant_plan_item`.`recurrence_pattern_id`=
				 `plan_item_recurrence_pattern`.`recurrence_pattern_id`
     WHERE
	 `participant_plan_item`.`repeat_type_id` = 'NONE' AND
     `participant_plan_item`.`status_id` != 4 AND 
     `participant_plan_item`.`pokket_user_id`=pokketUserId AND
     `participant_plan_item`.`plan_item_type_id`=4
     UNION
	 SELECT `participant_plan_item`.`participant_plan_item_id` as reminderId,
    `participant_plan_item`.`participant_plan_item_description` as description,
    `participant_plan_item`.`pokket_user_id` as pokketUserId,
    `plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
    `plan_item_recurrence`.`recurrence_id` as recurrenceId,
	 DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
    `plan_item_recurrence`.`is_original_plan_item` as isOriginalPlanItem,
    `plan_item_recurrence`.`location` as location,
    `plan_item_recurrence`.`due_date` as dueDate,
    `plan_item_recurrence`.`completion_date` as completionDate,
	`plan_item_recurrence`.`completion_notes` as completionNotes,
    `participant_plan_item`.`duration_in_minutes` as duration,
    if(`plan_item_recurrence`.`is_original_plan_item`=1 AND `plan_item_recurrence`.`status_id`=4,2, `plan_item_recurrence`.`status_id`) as `status`, 
    `plan_item_recurrence`.`notes` as `notes`,
    `participant_plan_item`.`repeat_interval` as repeatInterval,
    `participant_plan_item`.`repeat_type_id` as repeatType,
     inActiveRecurrenceIds
     FROM `participant_plan_item` inner join `plan_item_recurrence`
         on `participant_plan_item`.`participant_plan_item_id`
         =`plan_item_recurrence`.`plan_item_id`
         inner join `plan_item_recurrence_pattern` on
         `plan_item_recurrence`.`recurrence_pattern_id`=
         `plan_item_recurrence_pattern`.`recurrence_pattern_id`
      left join (SELECT plan_item_id,group_concat(recurrence_id) as inActiveRecurrenceIds
         FROM mypokket.plan_item_recurrence where status_id=4
			group by plan_item_id) d
		on  `participant_plan_item`.`participant_plan_item_id` = d.plan_item_id
     WHERE
     `participant_plan_item`.`repeat_type_id` != 'NONE' AND
     `participant_plan_item`.`pokket_user_id`=pokketUserId AND
     `participant_plan_item`.`plan_item_type_id`=4 
     )U
	  order by  STR_TO_DATE(dueDate,'%m/%d/%Y') ASC;
    end if;
    

END ;;
