CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetParticipantsAssignedToCM`(
		caseManagerPokketUserId varchar(50),awsRequestId varchar(50))
BEGIN

    SELECT  
    `pokket_user`.`pokket_user_id` as pokketUserId,
    `pokket_user`.`user_name` as userName,
    `pokket_user`.`email_address` as emailAddress,
    `pokket_user`.`pokket_user_status_id` as status,
    `pokket_user`.`name_prefix` as namePrefix,
    `pokket_user`.`first_name` as firstName,
    `pokket_user`.`middle_name` as middleName,
    `pokket_user`.`last_name` as lastName,
    `pokket_user`.`name_suffix` as nameSuffix,
    `pokket_user`.`preferred_name` as preferredName,
    `pokket_user`.`profile_image_url` as profileImageUrl,
    `pokket_user`.`primary_phone_number` as primaryPhoneNumber,
    `pokket_user`.`secondary_phone_number` as secondaryPhoneNumber,
	`pokket_user`.`is_sms_opt_in` as isSmsOptIn,
	`pokket_user`.`is_initial_sms_opt_in_acknowledged` as isInitialSmsOptInAcknowledged,
     GROUP_CONCAT(`pokket_user_role`.`user_role_id`) AS roles,
  	`participant`.`address_available` AS addressAvailable,
	`participant`.`resides_with` AS residesWith,
	`participant`.`ssn` AS ssn,
	`participant`.`phone_number_available` AS phoneNumberAvailable,
	`participant`.`birth_date` AS birthDate,
	`participant`.`state_of_birth` AS stateOfBirth,
	`participant`.`registered_for_selective_service` AS registeredForSelectiveService,
    `participant`.`registered_as_sex_offender` AS registeredAsSexOffender,
    `participant`.`is_veteran` AS isVeteran,
	`participant`.`no_of_family_adults` AS noOfAdultsInFamily,
	`participant`.`no_of_family_children` AS noOfChildrenInFamily,
	`participant`.`other_ethnic_background` AS otherEthnicBackground,
	`participant`.`enroll_date` AS enrollDate,
	`participant`.`no_of_dependents` AS noOfDependentsInFamily,
	`participant`.`educational_level_attained` AS educationLevel,
	`participant`.`occupation` AS occupation,
	`participant`.`gender` AS gender,
	`participant`.`race_id` AS raceId,
	`participant`.`household_income_id` as householdIncomeId,
    `participant`.`in_custody_status` as inCustodyStatus,
	`participant`.`contract_id` as contractId,
	`participant_task_counts`.`count_of_upcoming_tasks` as countOfUpcomingTasks,
    `participant_task_counts`.`count_of_late_tasks` as countOfPastDueTasks,
    IF(TIMESTAMPDIFF(SECOND, DATE_FORMAT(DATE_SUB(NOW(),INTERVAL 6 HOUR),"%Y-%m-%d 00:00:00"),IFNULL(participant_task_counts.task_counts_updated_dtm,'1900-01-01 00:00:00')) <0,1,0) as areCountsStale,
    serviceIds
	FROM `participant` 
     inner join `pokket_user`
     on `participant`.`pokket_user_id`=`pokket_user`.`pokket_user_id`
     inner join `pokket_user_role` 
	 on `pokket_user`.`pokket_user_id`=`pokket_user_role`.`pokket_user_id`
     left join participant_task_counts
     on  participant_task_counts.pokket_user_id = participant.pokket_user_id
     and participant_task_counts.shared_with_pokket_user_id = caseManagerPokketUserId
     left join 
     (select pokket_user_id,group_concat(service_id) as serviceIds from participant_referral 
       inner join organization_service on
       participant_referral.service_id=organization_service.organization_service_id
		where organization_service.service_status_id=2 and  is_service_assigned_to_participant=1
		and  case_manager_id=caseManagerPokketUserId
		group by pokket_user_id) A
		on participant.pokket_user_id=A.pokket_user_id 
    where pokket_user.pokket_user_status_id=4 and 
    `participant`.`pokket_user_id` IN
    (SELECT `pokket_user_connection`.`connection_pokket_user_id`
 	FROM `pokket_user_connection` inner join 
    `participant_referral` on 
    `pokket_user_connection`.`connection_pokket_user_id` = `participant_referral`.`pokket_user_id`
	WHERE `pokket_user_connection`.`pokket_user_id`=caseManagerPokketUserId
    AND `pokket_user_connection`.`connection_pokket_user_role_id`=5
	AND `pokket_user_connection`.`is_active`=1
