							participant_referral.is_service_assigned_to_participant=1 and facility_service_case_manager.is_active=1
							UNION
							SELECT     `vw_active_pokket_user_shares`.`share_id` as pokketUserShareId,
							`vw_active_pokket_user_shares`.`shared_by` as sharedByPokketUserId,
							 P1.first_name as sharedByFirstName,
							 P1.last_name as sharedByLastName,
