							FROM `vw_active_pokket_user_shares` inner join pokket_user P1 on
							`vw_active_pokket_user_shares`.`shared_by`= P1.pokket_user_id
							inner join organization_service on
							vw_active_pokket_user_shares.shared_with=organization_service.organization_service_id 
							inner join organization on 
							organization_service.organization_id=organization.organization_id
							inner join  participant_referral 
							on vw_active_pokket_user_shares.shared_by=participant_referral.pokket_user_id
							and vw_active_pokket_user_shares.shared_with=participant_referral.service_id
							inner join  facility_service_case_manager on
							participant_referral.facility_id=facility_service_case_manager.facility_id and
							participant_referral.service_id=facility_service_case_manager.service_id and 
							participant_referral.case_manager_id=facility_service_case_manager.pokket_user_id
							inner join pokket_user P2 on facility_service_case_manager.pokket_user_id=P2.pokket_user_id
							where share_type_id=3 and shared_ref_id=planItemId and 
							participant_referral.is_service_assigned_to_participant=1 and facility_service_case_manager.is_active=1
							) Z 
                             left join (select shared_with,shared_ref_id,max(share_type_id) as sharedTypeId from vw_active_pokket_user_shares
                             where shared_by=pokketUserId
                             and shared_ref_id in (planItemId,@parentPlanItem,@parentParentPlanItem) 
                             group by shared_with,shared_ref_id) X
                             on Z.sharedWithServiceId=X.shared_with and
                             Z.sharedRefId=X.shared_ref_id and
                             Z.shareType=X.sharedTypeId
                            where
                            Z.sharedWithServiceId not in 
                            (select shared_with from pokket_user_share
                            where is_active=0 and shared_ref_id in (planItemId,@parentPlanItem,@parentParentPlanItem)                        
                            and shared_with not in (select shared_with from vw_active_pokket_user_shares where 
                            
                            shared_ref_id in (planItemId)));
                             
                          
                           
                            
                            
						
						end if;
			   
				end if;


END ;;
