CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetPokketUser`(
		pokketUserId varchar(50),
viewerPokketUserId varchar(50),
awsRequestId varchar(50))
BEGIN

set
@isOrgProfileCompleted = null;
select
	count(*)
into
	@participantRoleCount
from
	pokket_user_role
where
	pokket_user_id = pokketUserId
	and user_role_id = 5 ;
if @participantRoleCount > 0 then
	select
	organization_id
into
	@organizationId
from
	pokket_user_organization
where
	pokket_user_id =(
	select
		created_by
	from
		pokket_user
	where
		pokket_user_id = pokketUserId);
	else
	select
	organization_id
into
	@organizationId
from
	pokket_user_organization
where
	pokket_user_id = pokketUserId;
    select
	sp_invite_status_id
into
	@spInviteStatusId
from
	organization_onboarding
where
	organization_id = @organizationId;
    if @spInviteStatusId = 6 then 
		set
	@isOrgProfileCompleted = 1;
	else 
		set
	@isOrgProfileCompleted = 0;
end if;
end if;

select
	organization_name
into
	@organizationName
from
	organization
where
	organization_id = @organizationId;
select
	if(count(*)>0,
	1,
	0)
into
	@isOrgRequiringAgency
from
	organization_role
where
	org_role_id = 2
	and organization_id = @organizationId;

    select
	pokket_user.pokket_user_id,
	first_name,
	last_name,
	primary_phone_number,
	email_address
into
	@primaryGuardianPokketUserId,
	@primaryGuardianFirstName,
	@primaryGuardianLastName,
	@primaryGuardianPhoneNumber,
	@primaryGuardianEmailAddress
from
	pokket_user
inner join participant on
	pokket_user.pokket_user_id = participant.primary_guardian_id
where
	participant.pokket_user_id = pokketUserId;

    select
	owners
INTO
	@owners
from
	(
	select
		@organizationId as node_org,
		group_concat(pokket_user.pokket_user_id) as owners
	from
		pokket_user
	inner join
pokket_user_organization on
		pokket_user.pokket_user_id = pokket_user_organization.pokket_user_id
	inner join 
pokket_user_role on
		pokket_user_organization.pokket_user_id = pokket_user_role.pokket_user_id
	where
		pokket_user.pokket_user_status_id = 4
		and
pokket_user_role.user_role_id = 3
		and pokket_user_organization.organization_id in (
		SELECT
			parent.organization_id
		FROM
			organization AS node,
			organization AS parent
		WHERE
			node.lft BETWEEN parent.lft AND parent.rgt
			AND node.organization_id = @organizationId
			and parent.organization_id != @organizationId
		ORDER BY
			parent.lft)
	group by
		node_org) A;

select
	organization_id
into
	@onboardedByOrgId
from
	pokket_user_organization
inner join pokket_user on
	pokket_user_organization.pokket_user_id = pokket_user.created_by
where
	pokket_user.pokket_user_id = pokketUserId;

select
	count(*)
into
	@countOfPhoneNumberChanges
from
	pokket_user_history
where
	pokket_user_id = pokketUserId
	and
old_primary_phone_number <> new_primary_phone_number;

if @countOfPhoneNumberChanges = 0 then
	select
	first_name ,
	last_name,
	primary_phone_number,
	user_name
into 
	@createdByFirstName,
	@createdByLastName,
	@createdByPhoneNumber,
	@createdByUserName
from
	pokket_user
where
	pokket_user_id in (
	select
		created_by
	from
		pokket_user
	where
		pokket_user_id = pokketUserId);
	else
	 select
	modified_by
into
	@modifiedBy
from
	pokket_user_history
where
	pokket_user_id = pokketUserId
	and
	old_primary_phone_number <> new_primary_phone_number
order by
	version desc
limit 1;
	select
	first_name ,
	last_name,
	primary_phone_number,
	user_name
into 
	@createdByFirstName,
	@createdByLastName,
	@createdByPhoneNumber,
	@createdByUserName
from
	pokket_user
where
	pokket_user_id = @modifiedBy;
end if;

 SELECT
	`pokket_user`.`is_cm_using_spa_phone`
into
	@isNotOwnPhone
from
	pokket_user
where
	pokket_user_id = pokketUserId; 

 SELECT
	`pokket_user_history`.`modified_dtm`
into
	@eulaAcceptanceDate
from
	pokket_user_history
where
	pokket_user_id = pokketUserId
	and old_is_eula_accepted = 0
	and new_is_eula_accepted = 1
order by
	pokket_user_history.modified_dtm desc
limit 1; 

 select
	count(*)
into
	@countOfParticipant
from
	participant
where
	pokket_user_id = pokketUserId; 
 if @countOfParticipant != 0 then
	select
	locate('+', primary_phone_number)
into
	@isValidPhone
from
	pokket_user
where
	pokket_user_id = pokketUserId;
	select
	in_custody_status
into
	@inCustodyStatus
from
	participant
where
	pokket_user_id = pokketUserId;
	if @isValidPhone = 1
	and @inCustodyStatus = 3 then
		set
	@isNotOwnPhone = 0;
	else
		set
	@isNotOwnPhone = 1;
end if;
end if;

     SELECT
	`pokket_user`.`pokket_user_id` as pokketUserId,
	`pokket_user`.`user_name` as userName,
	@eulaAcceptanceDate as eulaAcceptanceDate,
	@organizationId as organizationId,
	@organizationName as organizationName,
	@createdByFirstName as createdByFirstName,
	@createdByLastName as createdByLastName,
	@createdByPhoneNumber as createdByPhoneNumber,
	@createdByUserName as createdByUserName,
	 @primaryGuardianPokketUserId as primaryGuardianPokketUserId,
	@primaryGuardianFirstName as primaryGuardianFirstName,
	@primaryGuardianLastName as primaryGuardianLastName,
	 @primaryGuardianPhoneNumber as primaryGuardianPhoneNumber,
	@primaryGuardianEmailAddress as primaryGuardianEmailAddress,
	 @isOrgRequiringAgency as isOrgRequiringAgency,
	`pokket_user`.`email_address` as emailAddress,
	`pokket_user`.`pokket_user_status_id` as status,
	@isNotOwnPhone as isNotOwnPhone,
	`pokket_user`.`name_prefix` as namePrefix,
	`pokket_user`.`first_name` as firstName,
	`pokket_user`.`middle_name` as middleName,
	`pokket_user`.`last_name` as lastName,
	`pokket_user`.`name_suffix` as nameSuffix,
	`pokket_user`.`preferred_name` as preferredName,
	`pokket_user`.`profile_image_url` as profileImageUrl,
	`pokket_user`.`primary_phone_number` as primaryPhoneNumber,
	`pokket_user`.`secondary_phone_number` as secondaryPhoneNumber,
	`pokket_user`.`is_eula_accepted` as isEulaAccepted,
	`pokket_user`.`device_id` as deviceId,
	`pokket_user`.`is_device_registered` as isDeviceRegistered,
	`pokket_user`.`is_sms_opt_in` as isSmsOptIn,
	`pokket_user`.`is_initial_sms_opt_in_acknowledged` as isInitialSmsOptInAcknowledged,
	`pokket_user`.`is_case_reassignment_acknowledged` as isCaseReassignmentAcknowledged,
	`pokket_user`.`is_autoshare_unacknowledged` as isAutoshareUnacknowledged,
	`pokket_user`.`notify_interval` as notifyInterval,
	`pokket_user`.`is_initial_profile_completed` as isInitialProfileCompleted,
	`pokket_user`.`is_temp_password_reset` as isTempPasswordReset,
	`pokket_user`.`availability_statement` as availabilityStatement,
	GROUP_CONCAT(`pokket_user_role`.`user_role_id`) AS roles,
	@owners as ownerSpas,
	@onboardedByOrgId as onboardedByOrgId,
	`participant`.`is_dependent` as isDependent,
	`participant`.`is_guardian` as isGuardian,
	`participant`.`address_available` AS addressAvailable,
	`participant`.`contract_id` as contractId,
	`participant`.`resides_with` AS residesWith,
	`pokket_user`.`ssn` AS ssn,
	`participant`.`phone_number_available` AS phoneNumberAvailable,
	`participant`.`birth_date` AS birthDate,
	`participant`.`state_of_birth` AS stateOfBirth,
	`participant`.`registered_for_selective_service` AS registeredForSelectiveService,
	`participant`.`registered_as_sex_offender` AS registeredAsSexOffender,
	`participant`.`no_of_family_adults` AS noOfAdultsInFamily,
	`participant`.`no_of_family_children` AS noOfChildrenInFamily,
	`participant`.`other_ethnic_background` AS otherEthnicBackground,
	`participant`.`enroll_date` AS enrollDate,
	`participant`.`no_of_dependents` AS noOfDependentsInFamily,
	`participant`.`educational_level_attained` AS educationLevel,
	`participant`.`occupation` AS occupation,
	`participant`.`gender` AS gender,
	`participant`.`race_id` AS race,
	`participant`.`household_income_id` as householdIncome,
	`participant`.`in_custody_status` as inCustodyStatus,
	`participant`.`is_veteran` as isVeteran,
	`participant`.`count_of_upcoming_tasks` as countOfUpcomingTasks,
	`participant`.`count_of_late_tasks` as countOfPastDueTasks,
	`participant`.`is_prompted_onboard_service_flow` as isPromptedOnboardServiceFlow,
	IF(TIMESTAMPDIFF(SECOND,
	DATE_FORMAT(NOW(), "%Y-%m-%d 00:00:00"),
	IFNULL(participant.task_counts_updated_dtm, '1900-01-01 00:00:00')) <0,
	1,
	0) as areCountsStale,
	`pokket_user_organization`.`organization_id` as organizationId,
	`pokket_user_organization`.`isAdmin` as isAdmin,
	countOfUnreadNotifications,
	countOfUnreadMessages
FROM
	`pokket_user`
inner join 
    (
	select
		pokket_user.pokket_user_id,
		ifnull(CountOfUnreadNotifications, 0) as countOfUnreadNotifications,
		ifnull(CountOfUnreadMessages, 0) as countOfUnreadMessages
	from
		pokket_user
	left join 
(
		select
			pokket_user_id,
			count(*) as CountOfUnreadNotifications
		from
			notification
		where
			is_read = 0
		group by
			pokket_user_id) A
on
		pokket_user.pokket_user_id = A.pokket_user_id
	left join
(
		select
			pokket_user_id,
			count(*) as CountOfUnreadMessages
		from
			pokket_user_message
		where
			is_read = 0
			and message_direction = 'TO'
		group by
			pokket_user_id) B
on
		pokket_user.pokket_user_id = B.pokket_user_id
	where
		pokket_user.pokket_user_id = pokketUserId) A
on
	pokket_user.pokket_user_id = A.pokket_user_id
left join `participant` 
    on
	`participant`.`pokket_user_id` = `pokket_user`.`pokket_user_id`
inner join `pokket_user_role` 
     on
	`pokket_user`.`pokket_user_id` = `pokket_user_role`.`pokket_user_id`
left join `pokket_user_organization` on
	`pokket_user`.`pokket_user_id` = `pokket_user_organization`.`pokket_user_id`
WHERE
	`pokket_user`.`pokket_user_id` = pokketUserId
GROUP BY
	`pokket_user`.`pokket_user_id`;    

END ;;
