    serviceIds as sharedServicesCommaDelimited,
    serviceIds
	FROM `pokket_user`
    inner join `pokket_user_connection`
    on `pokket_user`.`pokket_user_id`=`pokket_user_connection`.`connection_pokket_user_id`
