    left join `participant` on 
    `pokket_user`.`pokket_user_id` = `participant`.`pokket_user_id`
    left join 
    ( select pokket_user_id,group_concat(serviceid) as serviceIds from (
        select pokket_user_id,service_id as serviceId from participant_referral 
		where  is_service_assigned_to_participant=1
		and  case_manager_id=pokketUserId
