		where  is_service_assigned_to_participant=1
		and  pokket_user_id=pokketUserId) B
        group by pokket_user_id) A
		on `pokket_user`.`pokket_user_id`=A.pokket_user_id
    where `pokket_user_connection`.`pokket_user_id` =pokketUserId
    and `pokket_user_connection`.`is_active`=1
    ORDER BY `pokket_user`.`last_name`; 

