CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetPokketUserDocuments`(
		pokketUserId varchar(50), needCategoryId varchar(50),
        awsRequestId varchar(50))
BEGIN
    SET @Query = CONCAT('SELECT `document`.`document_id` as documentId,
    `plan_item_document`.`participant_plan_item_id` as planItemId,
    `plan_item_document`.`recurrence_id` as recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
    `plan_item_document`.`service_plan_item_document_id` as servicePlanItemDocumentId,
    `participant_plan_item`.`need_category_id` as needCategory,',
    QUOTE(pokketUserId),' as pokketUserId,
    concat(`pokket_user`.`first_name`,',QUOTE(' '),',`pokket_user`.`last_name`) as uploadedBy,
    DATE_FORMAT(`document`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as createdDateTime,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
    `document`.`created_dtm`,
    `document`.`service_id` as serviceId,
	organization_service.service_name as serviceName,
	organization.organization_name as organizationName
	FROM `document` inner join `plan_item_document`
    on `document`.`document_id`=`plan_item_document`.`document_id`
    INNER JOIN `participant_plan_item` on
    `participant_plan_item`.`participant_plan_item_id`=
    `plan_item_document`.`participant_plan_item_id`
    inner join `pokket_user` on
    `document`.`created_by`=`pokket_user`.`pokket_user_id`
    left join organization_service on document.service_id= organization_service.organization_service_id 
	left join organization on organization_service.organization_id = organization.organization_id
	WHERE `document`.`is_active`=1 AND
	`participant_plan_item`.`pokket_user_id`=',QUOTE(pokketUserId));
    IF needCategoryId IS NOT NULL THEN
    SET @Query = CONCAT(@Query, ' AND
    `participant_plan_item`.`need_category_id`=',QUOTE(needCategoryId));
	END IF;
    SET @Query = CONCAT(@Query, ' UNION
    SELECT `document`.`document_id` as documentId,
     null AS  planItemId,
     null AS  recurrenceId,
    `document`.`document_name` as `name`,
    `document`.`document_url` as url,
    `document`.`is_active` as isActive,
     null as servicePlanItemDocumentId,
    `pokket_user_document`.`need_category_id` as needCategory,',
     QUOTE(pokketUserId),' as pokketUserId,
     concat(`pokket_user`.`first_name`,',QUOTE(' '),',`pokket_user`.`last_name`) as uploadedBy,
    DATE_FORMAT(`document`.`created_dtm`,',QUOTE('%m/%d/%Y %r'),') as createdDateTime,
	`document`.`is_scanned` as isScanned,
	`document`.`is_quarantined` as isQuarantined,
    `document`.`antivirus_scan_status` AS `antivirusScanStatus`,
    `document`.`created_dtm`,
    `document`.`service_id` as serviceId,
	organization_service.service_name as serviceName,
	organization.organization_name as organizationName
	FROM `document` inner join `pokket_user_document`
    on `document`.`document_id`=`pokket_user_document`.`document_id`
    inner join `pokket_user` on
    `document`.`created_by`=`pokket_user`.`pokket_user_id`
	left join organization_service on document.service_id= organization_service.organization_service_id 
	left join organization on organization_service.organization_id = organization.organization_id
	WHERE `document`.`is_active`=1 AND 
	`pokket_user_document`.`pokket_user_id`=', QUOTE(pokketUserId));
    IF needCategoryId IS NOT NULL THEN
    SET @Query = CONCAT(@Query, ' AND
    `pokket_user_document`.`need_category_id`=',QUOTE(needCategoryId));
	END IF;
    SET @Query = CONCAT(@Query, ' order by 9, 15 desc');
    
    PREPARE stmt FROM @Query;
	EXECUTE stmt;
END ;;
