CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetPokketUserEvent`(
		eventId varchar(50),pokketUserId varchar(50),awsRequestId varchar(50))
BEGIN

  select `pokket_user_event`.`participant_plan_item_id`,`pokket_user_event`.`appointment_request_id`,`recurrence_id`
  into @planItemId,@appointmentRequestId,@recurrenceId from `pokket_user_event`
  WHERE `pokket_user_event`.`event_id`=eventId and  
  `pokket_user_event`.`pokket_user_id`=pokketUserId;
  

  
  if @planItemId is not null then
		if @recurrenceId !='0' then
			  SELECT CONCAT(`participant_plan_item`.`participant_plan_item_id`,'|',ifnull(`plan_item_recurrence`.`recurrence_id`,'0')) as eventId,
				`participant_plan_item`.`pokket_user_id`as pokketUserId,
				LEFT(`participant_plan_item`.`participant_plan_item_description`,50) as eventTitle,
				`participant_plan_item`.`participant_plan_item_description` as eventDescription,
				`plan_item_recurrence`.`location` as eventLocation,
                `appointment_request`.`video_meeting_id` as videoMeetingId,
				'US/Eastern' as eventTimeZone,
			   ifnull(participant_plan_item.repeat_interval,0) as eventRepeatInterval,
				ifnull(participant_plan_item.repeat_type_id,'NONE') as eventRepeatType,
				if(`participant_plan_item`.`status_id` != 4,true,false) as isActive,
			   `participant_plan_item`.`participant_plan_item_id` as planItemId,
				ifnull(`plan_item_recurrence`.`recurrence_id`,'0') as recurrenceId,
			  DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
				`plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
				`plan_item_recurrence`.`is_original_plan_item` as isOriginalPlanItem,
				 null as appointmentRequestId,
				STR_TO_DATE(plan_item_recurrence.due_date, '%m/%d/%Y') as eventStartDateTime,
				DATE_ADD(STR_TO_DATE(plan_item_recurrence.due_date, '%m/%d/%Y'),INTERVAL participant_plan_item.duration_in_minutes MINUTE)  as eventEndDateTime,
				inActiveRecurrenceIds,
				invitedUser.pokket_user_id as inviteePokketUserId,
				invitedUser.first_name as inviteeFirstName,
				invitedUser.last_name as inviteeLastName,
				(SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitedUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviteeRole,
				invitingUser.pokket_user_id as inviterPokketUserId,
				invitingUser.first_name as inviterFirstName,
				invitingUser.last_name as inviterLastName,
				(SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitingUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviterRole,
				appointment_request.appointment_batch_id as appointmentBatchId
				 FROM `participant_plan_item` left join `plan_item_recurrence`
					 on `participant_plan_item`.`participant_plan_item_id`
					 =`plan_item_recurrence`.`plan_item_id`
					 left join `plan_item_recurrence_pattern` on
					 `plan_item_recurrence`.`recurrence_pattern_id`=
					 `plan_item_recurrence_pattern`.`recurrence_pattern_id`
					 left join (SELECT plan_item_id,group_concat(recurrence_id) as inActiveRecurrenceIds
					 FROM mypokket.plan_item_recurrence where status_id=4
						group by plan_item_id) d
					on  `participant_plan_item`.`participant_plan_item_id` = d.plan_item_id
                    JOIN `appointment_request` ON `participant_plan_item`.`appointment_request_id` = `appointment_request`.`appointment_request_id`
					left join pokket_user invitingUser on appointment_request.created_by=invitingUser.pokket_user_id
					left join pokket_user invitedUser on appointment_request.to_pokket_user_id=invitedUser.pokket_user_id
                 WHERE  
				 `participant_plan_item`.`pokket_user_id`=pokketUserId AND
				 `participant_plan_item`.`participant_plan_item_id`=@planItemId AND
				 ifnull(`plan_item_recurrence`.`is_original_plan_item`,1)=1 ;
		 else
				SELECT `pokket_user_event`.`event_id` as eventId,
			`pokket_user_event`.`pokket_user_id`as pokketUserId,
			`pokket_user_event`.`event_title` as eventTitle,
			`pokket_user_event`.`event_description` as eventDescription,
			`pokket_user_event`.`event_location` as eventLocation,
            `appointment_request`.`video_meeting_id` as videoMeetingId,
			`pokket_user_event`.`event_tz` as eventTimeZone,
			`pokket_user_event`.`ends_at_dtm` as eventEndDateTime,
			`pokket_user_event`.`event_repeat_interval` as eventRepeatInterval,
			`pokket_user_event`.`event_repeat_type_id` as eventRepeatType,
			`pokket_user_event`.`is_active` as isActive,
			`pokket_user_event`.`participant_plan_item_id` as planItemId,
			'0' as recurrenceId,
			 null as recurrenceEndDate,
			null as recurrencePatternRule,
			null as isOriginalPlanItem,
			`pokket_user_event`.`appointment_request_id` as appointmentRequestId,
			`pokket_user_event`.`starts_at_dtm` as eventStartDateTime,
			`pokket_user_event`.`ends_at_dtm` as eventEndDateTime,
			'' as inActiveRecurrenceIds,
			invitedUser.pokket_user_id as inviteePokketUserId,
			invitedUser.first_name as inviteeFirstName,
			invitedUser.last_name as inviteeLastName,
			(SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitedUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviteeRole,
			invitingUser.pokket_user_id as inviterPokketUserId,
