CREATE DEFINER=`acivilatedbadmin`@`%` PROCEDURE `uspGetPokketUserEvents`(
		pokketUserId varchar(50),
        startDate datetime,
        awsRequestId varchar(50),
        viewerPokketUserId varchar(50))
BEGIN


	select count(*) into @countOfParticipantRole from pokket_user_role 
    where pokket_user_id=pokketUserId and user_role_id=5;
    
    
    if @countOfParticipantRole > 0 and viewerPokketUserId = pokketUserId  then
    SELECT *
    FROM (
    SELECT CONCAT(`participant_plan_item`.`participant_plan_item_id`,'|','0') as eventId,
    `participant_plan_item`.`pokket_user_id`as pokketUserId,
    LEFT(`participant_plan_item`.`participant_plan_item_description`,50) as eventTitle,
    `participant_plan_item`.`participant_plan_item_description` as eventDescription,
    `participant_plan_item`.`location` as eventLocation,
    'US/Eastern' as eventTimeZone,
    participant_plan_item.duration_in_minutes as duration,
    ifnull(participant_plan_item.repeat_interval,0) as eventRepeatInterval,
    ifnull(participant_plan_item.repeat_type_id,'NONE') as eventRepeatType,
    appointment_request.appointment_request_id as appointmentRequestId,
    `participant_plan_item`.`participant_plan_item_id` as planItemId,
    (select appointment_request.video_meeting_id 
    from participant_plan_item 
	JOIN appointment_request 
	ON participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
	WHERE participant_plan_item.participant_plan_item_id=planItemId) as videoMeetingId,
    0 as recurrenceId,
    `plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
     DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
	1 as isOriginalPlanItem,
    STR_TO_DATE(participant_plan_item.due_date,  '%m/%d/%Y %h:%i %p') as eventStartDateTime,
    DATE_ADD(STR_TO_DATE(participant_plan_item.due_date,  '%m/%d/%Y %h:%i %p'),INTERVAL participant_plan_item.duration_in_minutes MINUTE)  as eventEndDateTime,
     STR_TO_DATE(participant_plan_item.due_date,  '%m/%d/%Y %h:%i %p') as startDateTime,
     '' as inActiveRecurrenceIds,
          B.inviteePokketUserId as inviteePokketUserId,
         B.inviteeFirstName as inviteeFirstName,
         B.inviteeLastName as inviteeLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=B.inviteePokketUserId AND (user_role_id=4 OR user_role_id=5)) AS inviteeRole,
		 null as serviceId,
         CONCAT(invitingUser.first_name,' ',invitingUser.last_name) as createdBy,
         invitingUser.pokket_user_id as inviterPokketUserId,
         invitingUser.first_name as inviterFirstName,
         invitingUser.last_name as inviterLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitingUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviterRole,
		 1 as isAppointmentWithAssignedCm,
         IF(`participant_plan_item`.`plan_item_type_id` = 4 , 1, 0) as isReminder,
         if(`participant_plan_item`.`status_id` = 3, 1,0) as isCompleted,
         appointment_request.appointment_request_status_id as appointmentRequestStatus,
		 appointment_request.appointment_batch_id as appointmentBatchId
	 FROM `participant_plan_item`
     left join `plan_item_recurrence_pattern` on
				 `participant_plan_item`.`recurrence_pattern_id`=
				 `plan_item_recurrence_pattern`.`recurrence_pattern_id`
              left join appointment_request on participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
				left join pokket_user invitingUser on appointment_request.from_pokket_user_id = invitingUser.pokket_user_id
         left join pokket_user_event on appointment_request.appointment_request_id = pokket_user_event.appointment_request_id
             left join (	  SELECT `pokket_user_event_invitee`.`event_id`,
     `pokket_user_event_invitee`.`invitee_pokket_user_id` as inviteePokketUserId,
     `pokket_user`.`first_name` as inviteeFirstName,
    `pokket_user`.`last_name` as inviteeLastName
	 FROM `pokket_user_event_invitee` inner join `pokket_user`
	 on `pokket_user_event_invitee`.`invitee_pokket_user_id`=`pokket_user`.`pokket_user_id`
     inner join pokket_user_role on
     `pokket_user_event_invitee`.`invitee_pokket_user_id`= pokket_user_role.pokket_user_id 
     where `pokket_user_event_invitee`.`invitee_pokket_user_id` !=pokketUserId 
      and pokket_user_role.user_role_id <> 5
     ) B
     on pokket_user_event.event_id=B.event_id                 
  	 WHERE  `participant_plan_item`.`repeat_type_id` = 'NONE' AND
      `participant_plan_item`.`status_id` != 4 AND 
     `participant_plan_item`.`pokket_user_id`=pokketUserId AND
     `participant_plan_item`.`plan_item_type_id` in (3,4)
 UNION
	 SELECT CONCAT(`participant_plan_item`.`participant_plan_item_id`,'|',`plan_item_recurrence`.`recurrence_id`) as eventId,
    `participant_plan_item`.`pokket_user_id`as pokketUserId,
    LEFT(`participant_plan_item`.`participant_plan_item_description`,50) as eventTitle,
    `participant_plan_item`.`participant_plan_item_description` as eventDescription,
    `plan_item_recurrence`.`location` as eventLocation,
	'US/Eastern' as eventTimeZone,
participant_plan_item.duration_in_minutes as duration,
    ifnull(participant_plan_item.repeat_interval,0) as eventRepeatInterval,
    ifnull(participant_plan_item.repeat_type_id,'NONE') as eventRepeatType,
    appointment_request.appointment_request_id as appointmentRequestId,
    `participant_plan_item`.`participant_plan_item_id` as planItemId,
     (select appointment_request.video_meeting_id 
    from participant_plan_item 
	JOIN appointment_request 
	ON participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
	WHERE participant_plan_item.participant_plan_item_id=planItemId) as videoMeetingId,
    `plan_item_recurrence`.`recurrence_id` as recurrenceId,
    `plan_item_recurrence_pattern`.`recurrence_pattern_rule` as recurrencePatternRule,
     DATE_FORMAT(`participant_plan_item`.`recurrence_end_date`,'%m/%d/%Y %T') as recurrenceEndDate,
	`plan_item_recurrence`.`is_original_plan_item` as isOriginalPlanItem,
    STR_TO_DATE(plan_item_recurrence.due_date,  '%m/%d/%Y %h:%i %p') as eventStartDateTime,
    DATE_ADD(STR_TO_DATE(plan_item_recurrence.due_date,  '%m/%d/%Y %h:%i %p'),INTERVAL participant_plan_item.duration_in_minutes MINUTE)  as eventEndDateTime,
     STR_TO_DATE(plan_item_recurrence.due_date,  '%m/%d/%Y %h:%i %p') as startDateTime,
     inActiveRecurrenceIds,
          B.inviteePokketUserId as inviteePokketUserId,
         B.inviteeFirstName as inviteeFirstName,
         B.inviteeLastName as inviteeLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=B.inviteePokketUserId AND (user_role_id=4 OR user_role_id=5)) AS inviteeRole,
		 null as serviceId,
         CONCAT(invitingUser.first_name,' ',invitingUser.last_name) as createdBy,
         invitingUser.pokket_user_id as inviterPokketUserId,
         invitingUser.first_name as inviterFirstName,
         invitingUser.last_name as inviterLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitingUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviterRole,
		 1 as isAppointmentWithAssignedCm,
         IF(`participant_plan_item`.`plan_item_type_id` = 4 , 1, 0) as isReminder,
         if(`plan_item_recurrence`.`status_id` = 3, 1,0) as isCompleted,
         appointment_request.appointment_request_status_id as appointmentRequestStatus,
		 appointment_request.appointment_batch_id as appointmentBatchId
	 FROM `participant_plan_item` inner join `plan_item_recurrence`
         on `participant_plan_item`.`participant_plan_item_id`
         =`plan_item_recurrence`.`plan_item_id`
         inner join `plan_item_recurrence_pattern` on
         `plan_item_recurrence`.`recurrence_pattern_id`=
         `plan_item_recurrence_pattern`.`recurrence_pattern_id`
		 left join appointment_request on participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
         left join pokket_user invitingUser on appointment_request.from_pokket_user_id = invitingUser.pokket_user_id
         left join pokket_user_event on appointment_request.appointment_request_id = pokket_user_event.appointment_request_id
         left join (SELECT plan_item_id,group_concat(recurrence_id) as inActiveRecurrenceIds
         FROM mypokket.plan_item_recurrence where status_id=4
			group by plan_item_id) d
		on  `participant_plan_item`.`participant_plan_item_id` = d.plan_item_id
         left join (	  SELECT `pokket_user_event_invitee`.`event_id`,
     `pokket_user_event_invitee`.`invitee_pokket_user_id` as inviteePokketUserId,
     `pokket_user`.`first_name` as inviteeFirstName,
    `pokket_user`.`last_name` as inviteeLastName
	 FROM `pokket_user_event_invitee` inner join `pokket_user`
	 on `pokket_user_event_invitee`.`invitee_pokket_user_id`=`pokket_user`.`pokket_user_id`
     inner join pokket_user_role on
     `pokket_user_event_invitee`.`invitee_pokket_user_id`= pokket_user_role.pokket_user_id 
     where `pokket_user_event_invitee`.`invitee_pokket_user_id` !=pokketUserId 
      and pokket_user_role.user_role_id <> 5
     ) B
     on pokket_user_event.event_id=B.event_id
  	 WHERE  `participant_plan_item`.`repeat_type_id` != 'NONE' AND
     `participant_plan_item`.`pokket_user_id`=pokketUserId AND
     `participant_plan_item`.`plan_item_type_id` in (3,4)

     UNION
     SELECT appointment_request.appointment_request_id as eventId,
    pokketUserId as pokketUserId,
    LEFT(`appointment_request`.`meeting_purpose`,50) as eventTitle,
    `appointment_request`.`meeting_purpose` as eventDescription,
    `appointment_request`.`location` as eventLocation,
	'US/Eastern' as eventTimeZone,
	appointment_request.duration_in_minutes as duration,
    0 as eventRepeatInterval,
    'NONE' as eventRepeatType,
    appointment_request.appointment_request_id as appointmentRequestId,
    null as planItemId,
    appointment_request.video_meeting_id as videoMeetingId,
    0 as recurrenceId,
    null as recurrencePatternRule,
	null as recurrenceEndDate,
	null as isOriginalPlanItem,
    STR_TO_DATE(appointment_request_timeslot.timeslot,  '%m/%d/%Y %h:%i %p') as eventStartDateTime,
    DATE_ADD(STR_TO_DATE(appointment_request_timeslot.timeslot,  '%m/%d/%Y %h:%i %p'),INTERVAL appointment_request.duration_in_minutes MINUTE) as eventEndDateTime,
	STR_TO_DATE(appointment_request_timeslot.timeslot,  '%m/%d/%Y %h:%i %p') as startDateTime,
    '' as inActiveRecurrenceIds,
          invitedUser.pokket_user_id as inviteePokketUserId,
         invitedUser.first_name as inviteeFirstName,
         invitedUser.last_name as inviteeLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitedUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviteeRole,
		 appointment_request.service_id as serviceId,
         CONCAT(invitingUser.first_name,' ',invitingUser.last_name) as createdBy,
         invitingUser.pokket_user_id as inviterPokketUserId,
         invitingUser.first_name as inviterFirstName,
         invitingUser.last_name as inviterLastName,
         (SELECT user_role_id FROM pokket_user_role WHERE pokket_user_id=invitingUser.pokket_user_id AND (user_role_id=4 OR user_role_id=5)) AS inviterRole,
		 appointment_request.is_with_assigned_cm as isAppointmentWithAssignedCm,
         0 as isReminder,
         0 as isCompleted,
         appointment_request.appointment_request_status_id as appointmentRequestStatus,
		 appointment_request.appointment_batch_id as appointmentBatchId
	 FROM appointment_request
		join pokket_user invitedUser ON appointment_request.to_pokket_user_id = invitedUser.pokket_user_id
        join appointment_request_timeslot ON appointment_request_timeslot.appointment_request_id = appointment_request.appointment_request_id
        left join participant_plan_item on participant_plan_item.appointment_request_id = appointment_request.appointment_request_id
		inner join pokket_user invitingUser on appointment_request.from_pokket_user_id = invitingUser.pokket_user_id
     WHERE 
     appointment_request.appointment_request_status_id = 1 AND
     participant_plan_item.participant_plan_item_id is null AND
     (`appointment_request`.`from_pokket_user_id` = pokketUserId OR
     `appointment_request`.`to_pokket_user_id` = pokketUserId)
     ) A
     where startDateTime between  date_sub(startDate, INTERVAL 2 MONTH) 
     AND date_ADD(startDate, INTERVAL 2 MONTH) 
     order by  startDateTime;
     
elseif @countOfParticipantRole > 0 and viewerPokketUserId <> pokketUserId  then 
     
     	select service_list INTO @serviceIds from (
		SELECT pokket_user_id, GROUP_CONCAT(service_id) as service_list
		from facility_service_case_manager where
		is_active=1 and pokket_user_id=viewerPokketUserId 
		group by pokket_user_id) AS A;
        
         select count(*) into @countOfActiveWholePlanShares from vw_active_pokket_user_shares 
            where share_type_id=1 and shared_by=pokketUserId and  FIND_IN_SET(shared_with,@serviceIds);
     
      SELECT *
    FROM (
    SELECT CONCAT(`participant_plan_item`.`participant_plan_item_id`,'|','0') as eventId,
    `participant_plan_item`.`pokket_user_id`as pokketUserId,
    LEFT(`participant_plan_item`.`participant_plan_item_description`,50) as eventTitle,
    `participant_plan_item`.`participant_plan_item_description` as eventDescription,
    `participant_plan_item`.`location` as eventLocation,
    'US/Eastern' as eventTimeZone,
    participant_plan_item.duration_in_minutes as duration,
    ifnull(participant_plan_item.repeat_interval,0) as eventRepeatInterval,
    ifnull(participant_plan_item.repeat_type_id,'NONE') as eventRepeatType,
    appointment_request.appointment_request_id as appointmentRequestId,
    `participant_plan_item`.`participant_plan_item_id` as planItemId,
    (select appointment_request.video_meeting_id 
    from participant_plan_item 
