     `pokket_user`.`pokket_user_id`=`pokket_user_organization`.`pokket_user_id`
	 WHERE `pokket_user_organization`.`organization_id`=organizationId
     and find_in_set(`vw_pokket_user_roles`.`user_role_id`, roles) !=0
     and `pokket_user`.`pokket_user_status_id` !=6 
     GROUP BY `pokket_user`.`pokket_user_id`
     union
     SELECT  `pokket_user`.`pokket_user_id` as pokketUserId,
    `pokket_user`.`pokket_user_status_id` as status,
    `pokket_user`.`name_prefix` as namePrefix,
